Method.Source:
public void init()
{
    QueryBuildDataSource                qbdsRentalTrans, qbdsRentalType, qbdsPostingPolicy, qbdsPolicyRule;
    BOBTmpRentalInventOnHand            tmpRentalInventOnHand;
    BOBRentalSum                        rentalSum;
    BOBTmpObjectAvailability_Graphical  tmpObjAvail;
    str                                 queryRangeExpr;
    TransDateTime                       nullDateTime;

    #DNRConstants

    super();

    switch(element.args().dataset()) // modify the default query to only show transactions attached to the calling record
    {
        case tableNum(SalesLine):
            qbdsRentalTrans = this.queryBuildDataSource().addDataSource(tableNum(InventTransOriginSalesLine));
            qbdsRentalTrans.joinMode(JoinMode::ExistsJoin);
            qbdsRentalTrans.relations(false);
            qbdsRentalTrans.addLink(fieldNum(BOBRentalTrans, InventTransOrigin), fieldNum(InventTransOriginSalesLine, InventTransOrigin));
            qbdsRentalTrans.addDynalink(fieldNum(InventTransOriginSalesLine, SalesLineDataAreaId), element.args().record(), fieldNum(SalesLine, DataAreaId));
            qbdsRentalTrans.addDynalink(fieldNum(InventTransOriginSalesLine, SalesLineInventTransId), element.args().record(), fieldNum(SalesLine, InventTransId));
            break;

        case tableNum(PurchLine):
            qbdsRentalTrans = this.queryBuildDataSource().addDataSource(tableNum(InventTransOriginPurchLine));
            qbdsRentalTrans.joinMode(JoinMode::ExistsJoin);
            qbdsRentalTrans.relations(false);
            qbdsRentalTrans.addLink(fieldNum(BOBRentalTrans, InventTransOrigin), fieldNum(InventTransOriginPurchLine, InventTransOrigin));
            qbdsRentalTrans.addDynalink(fieldNum(InventTransOriginPurchLine, PurchLineDataAreaId), element.args().record(), fieldNum(PurchLine, DataAreaId));
            qbdsRentalTrans.addDynalink(fieldNum(InventTransOriginPurchLine, PurchLineInventTransId), element.args().record(), fieldNum(PurchLine, InventTransId));
            break;

        case tableNum(SalesQuotationLine):
            qbdsRentalTrans = this.queryBuildDataSource().addDataSource(tableNum(InventTransOriginSalesQuotationLine));
            qbdsRentalTrans.joinMode(JoinMode::ExistsJoin);
            qbdsRentalTrans.relations(false);
            qbdsRentalTrans.addLink(fieldNum(BOBRentalTrans, InventTransOrigin), fieldNum(InventTransOriginSalesQuotationLine, InventTransOrigin));
            qbdsRentalTrans.addDynalink(fieldNum(InventTransOriginSalesQuotationLine, SalesQuotationLineDataAreaId), element.args().record(), fieldNum(SalesQuotationLine, DataAreaId));
            qbdsRentalTrans.addDynalink(fieldNum(InventTransOriginSalesQuotationLine, SalesQuotationLineInventTransId), element.args().record(), fieldNum(SalesQuotationLine, InventTransId));
            break;

        case tableNum(BOBObjectView):
            this.queryBuildDataSource().addDynaLink(fieldNum(BOBObjectView, ObjectId), element.args().record(), fieldNum(BOBRentalTrans, ObjectId));
            break;

        case tableNum(BOBObjectViewV2):
            this.queryBuildDataSource().addDynalink(fieldNum(BOBObjectViewV2, ObjectId), element.args().record(), fieldNum(BOBRentalTrans, ObjectId));
            break;

        case tableNum(BOBTmpObjectAvailability):
            qbdsRentalTrans = this.queryBuildDataSource().addDataSource(tableNum(BOBObjectTable));
            qbdsRentalTrans.joinMode(JoinMode::ExistsJoin);
            qbdsRentalTrans.relations(true);
            qbdsRentalTrans.addDynaLink(fieldNum(BOBObjectView, RecId), element.args().record(), fieldNum(BOBTmpObjectAvailability, ObjectTableRecId));
            break;

        case tableNum(BOBTmpRentalInventOnHand) :
            tmpRentalInventOnHand = element.args().record();
            switch (element.args().parmEnum())
            {
                case BOBRentalOnHandStatus::NotYetReturned:
                    qbdsRentalTrans = this.queryBuildDataSource();
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, RentalTypeId)).value(SysQuery::value(tmpRentalInventOnHand.RentalTypeId));
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, InventLocationId)).value(SysQuery::value(tmpRentalInventOnHand.InventLocationId));
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, TransType)).value(SysQuery::value(BOBRentalTransType::Sales));
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, Active)).value(SysQuery::value(BOBRentalTransActive::Active));
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, CertaintyTo)).value(SysQuery::value(BOBDateTimeCertainty::Firm));
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, ToDateTime)).value(SysQuery::range(DateTimeUtil::minValue(), DateTimeUtil::newDateTime(tmpRentalInventOnHand.TransDate, #INVTimeMax, DateTimeUtil::getUserPreferredTimeZone())));
                    // substatus lower than Returned
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, SubStatus)).value(SysQuery::range(BOBRentalSubStatus::Built, BOBRentalSubStatus::Inbound));
                    break;

                case BOBRentalOnHandStatus::NotYetDelivered:
                    qbdsRentalTrans = this.queryBuildDataSource();
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, RentalTypeId)).value(SysQuery::value(tmpRentalInventOnHand.RentalTypeId));
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, InventLocationId)).value(SysQuery::value(tmpRentalInventOnHand.InventLocationId));
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, TransType)).value(SysQuery::value(BOBRentalTransType::Sales));
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, Active)).value(SysQuery::value(BOBRentalTransActive::Active));
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, DataAreaId)).value(strFmt('(((%1 == %2) || (%3 > %4)) && (%5 <= %6))',
                                                                                                  fieldStr(BOBRentalTrans, CertaintyTo),
                                                                                                  any2int(BOBDateTimeCertainty::Tentative),
                                                                                                  fieldStr(BOBRentalTrans, ToDateTime),
                                                                                                  DateTimeUtil::toStr(DateTimeUtil::newDateTime(tmpRentalInventOnHand.TransDate, 0, DateTimeUtil::getUserPreferredTimeZone())),
                                                                                                  fieldStr(BOBRentalTrans, FromDateTime),
                                                                                                  DateTimeUtil::toStr(DateTimeUtil::newDateTime(tmpRentalInventOnHand.TransDate, #INVTimeMax, DateTimeUtil::getUserPreferredTimeZone()))));
                    // substatus lower than Delivered
                    qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, SubStatus)).value(SysQuery::range(0, BOBRentalSubStatus::Outbound));
                    break;
                }
            break;

        case tableNum(InventTransferLine):
            InventTransferLine inventTransferLine = element.args().record();
            qbdsRentalTrans = this.queryBuildDataSource().addDataSource(tableNum(InventTransOrigin));
            qbdsRentalTrans.joinMode(JoinMode::ExistsJoin);
            qbdsRentalTrans.relations(false);
            qbdsRentalTrans.addLink(fieldNum(BOBRentalTrans, InventTransOrigin), fieldNum(InventTransOrigin, RecId));
            qbdsRentalTrans.addRange(fieldNum(InventTransOrigin, DataAreaId)).value(strFmt('(%1.%2 == \"%3\") || (%1.%2 == \"%4\") || (%1.%2 == \"%5\") || (%1.%2 == \"%6\")',
                                                                                             qbdsRentalTrans.name(),
                                                                                             fieldStr(InventTransOrigin, InventTransId),
                                                                                             inventTransferLine.InventTransIdReceive,
                                                                                             inventTransferLine.InventTransId,
                                                                                             inventTransferLine.InventTransIdTransitFrom,
                                                                                             inventTransferLine.InventTransIdTransitTo));
            break;

        case tableNum(BOBRentalSum):
            rentalSum = element.args().record();
            qbdsRentalTrans = this.queryBuildDataSource();
            qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, RentalTypeId)).value(rentalSum.RentalTypeId);
            qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, InventLocationId)).value(rentalSum.InventLocationId);
            break;

        case tableNum(BOBTmpObjectAvailability_Graphical):
            tmpObjAvail = element.args().record();
            nullDateTime = DateTimeUtil::addDays(utcDateTimeNull(), 1);

            qbdsRentalTrans = this.queryBuildDataSource();
            qbdsRentalTrans.clearDynalinks();
            qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, Active)).value(SysQuery::value(BOBRentalTransActive::Active));
            qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, RentalTypeId)).value(tmpObjAvail.RentalTypeId);
            qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, InventLocationId)).value(tmpObjAvail.InventLocationId);
            qbdsRentalTrans.addRange(fieldNum(BOBRentalTrans, DataAreaId)).value(strFmt('((%1 < %2) && (%3 > %4))',
                                                                                        fieldStr(BOBRentalTrans, FromDateTime),
                                                                                        DateTimeUtil::toStr(tmpObjAvail.ToDateTime),
                                                                                        fieldStr(BOBRentalTrans, ToDateTime),
                                                                                        DateTimeUtil::toStr(tmpObjAvail.FromDateTime ? tmpObjAvail.FromDateTime : nullDateTime)));
            qbdsRentalType = qbdsRentalTrans.addDataSource(tableNum(BOBRentalType));
            qbdsRentalType.joinMode(JoinMode::ExistsJoin);
            qbdsRentalType.relations(true);

            qbdsPostingPolicy = qbdsRentalType.addDataSource(tableNum(BOBPostingPolicy));
            qbdsPostingPolicy.relations(true);
            qbdsPostingPolicy.addSelectionField(fieldNum(BOBPostingPolicy, TableId));
            qbdsPostingPolicy.addRange(fieldNum(BOBPostingPolicy, IncludeInAvailabilityOverview)).value(SysQuery::value(NoYes::Yes));

            queryRangeExpr = strFmt('((%1 == %2.%3) && (((%2.%4 == %5) && (%6 == %7)) || ((%2.%4 == %8) && (%9 == %7)) || ((%2.%4 == %10) && (%11 == %7)) || ((%2.%4 == %12) && (%13 == %7)) || ((%2.%4 == %14) && (%15 == %7)) || (%2.%4 == %16) || (%2.%4 == %17) || (%2.%4 == %18)))',
                                    fieldStr(BOBPostingPolicyRule, SubStatus),
                                    qbdsRentalTrans.name(),
                                    fieldStr(BOBRentalTrans, SubStatus),
                                    fieldStr(BOBRentalTrans, TransType),
                                    any2int(BOBRentalTransType::Rental),
                                    fieldStr(BOBPostingPolicyRule, IsAvailableRental),
                                    any2int(NoYes::Yes),
                                    any2int(BOBRentalTransType::SubRental),
                                    fieldStr(BOBPostingPolicyRule, IsAvailableSubRental),
                                    any2int(BOBRentalTransType::Sales),
                                    fieldStr(BOBPostingPolicyRule, IsAvailableSales),
                                    any2int(BOBRentalTransType::Purch),
                                    fieldStr(BOBPostingPolicyRule, IsAvailablePurchase),
                                    any2int(BOBRentalTransType::WPPWorkOrder),
                                    fieldStr(BOBPostingPolicyRule, IsAvailableWorkOrder),
                                    any2int(BOBRentalTransType::InventCounting),
                                    any2int(BOBRentalTransType::Invent),
                                    any2int(BOBRentalTransType::Asset));

            qbdsPolicyRule = qbdsPostingPolicy.addDataSource(tableNum(BOBPostingPolicyRule));
            qbdsPolicyRule.relations(true);
            qbdsPolicyRule.addSelectionField(fieldNum(BOBPostingPolicyRule, TableId));
            qbdsPolicyRule.addRange(fieldNum(BOBPostingPolicyRule, DataAreaId)).value(queryRangeExpr);
            break;

        case tableNum(BOBRentalTrans):
            qbdsRentalTrans = this.queryBuildDataSource();
            qbdsRentalTrans.clearDynalinks(); // creates a dynalink on RentalTrans.InventTransferLink by default
            qbdsRentalTrans.addDynalink(fieldNum(BOBRentalTrans, RecId), element.args().record(), fieldNum(BOBRentalTrans, RecId));
            break;

        case tableNum(InventJournalTrans):
            qbdsRentalTrans = this.queryBuildDataSource().addDataSource(tableNum(InventTransOrigin));
            qbdsRentalTrans.joinMode(JoinMode::ExistsJoin);
            qbdsRentalTrans.relations(false);
            qbdsRentalTrans.addLink(fieldNum(BOBRentalTrans, InventTransOrigin), fieldNum(InventTransOrigin, RecId));
            qbdsRentalTrans.addDynalink(fieldNum(InventTransOrigin, InventTransId), element.args().record(), fieldNum(InventJournalTrans, InventTransId));
            break;

        case tableNum(ProdTable):
            qbdsRentalTrans = this.queryBuildDataSource().addDataSource(tableNum(InventTransOriginProdTable));
            qbdsRentalTrans.joinMode(JoinMode::ExistsJoin);
            qbdsRentalTrans.relations(false);
            qbdsRentalTrans.addLink(fieldNum(BOBRentalTrans, InventTransOrigin), fieldNum(InventTransOriginProdTable, InventTransOrigin));
            qbdsRentalTrans.addDynalink(fieldNum(InventTransOriginProdTable, ProdOrderDataAreaId), element.args().record(), fieldNum(ProdTable, DataAreaId));
            qbdsRentalTrans.addDynalink(fieldNum(InventTransOriginProdTable, ProdOrderId), element.args().record(), fieldNum(ProdTable, ProdId));
            break;

        default:
            // don't modify the default query
            break;
    }
}

public int active()
{
    int ret;

    ret = super();

    ButtonOrigin.enabled(RentalTrans.hasValidOrigin());
    return ret;
}

// The only reason why 'BOBObjectTable' has been added as a data source to the form, is to display the fleet number in the grid.
// Even though 'Allow Create', 'Allow Delete' and 'Allow Edit' on the form data source are set to 'No', the 'validateWrite()' 
// method on 'BOBObjectTable' is still triggered when you save a record in the form. Since 'Only Fetch Active' is set to 'Yes',
// only the fleet number is fetched from the database and this causes problems when the 'validateWrite()' method on 'BOBObjectTable'
// is called. Since no record is inserted in 'BOBObjectTable' from this form, simply return 'true' (to supress unwanted warnings). 
public boolean validateWrite()
{
    return true;
}
