// Splitter
public void close()
{
    FormRun         callerForm;
    FormDataSource  formDataSource;
    Args            args;

    args = element.args();

    if (args && args.caller() && args.record())
    {
        callerForm = args.caller();
        formDataSource = callerForm.dataSource();
        formDataSource.reread();
        formDataSource.refresh();
    }

    super();
}

[SuppressBPWarningAttribute("BPCheckInvalidInitFormMethodWarning", "Method was duplicated from standard AX")]
public void init()
{
    element.initCaller();
    element.initFormClass();

    super();

    element.initDesign();
}

private void initCaller()
{
    BSAContractView contractView;
    ;

    switch(element.args().dataset())
    {
        case tableNum(BSAContractTable):
            contractTable = element.args().record();
            break;

        case tableNum(BSAContractView):
            contractView  = element.args().record();
            contractTable = BSAContractTable::find(contractView.ContractId);
            break;

        default:
            throw error(strFmt("@DNR3705", element.name(), tableStr(BSAContractTable), tableStr(BSAContractView)));
    }
}

private void initDesign()
{

    TierSubscription.visible(contractTable.BusRelType == TableGroupAll::Table);
}

private void initFormClass()
{
    BSAContractDataParm contractDataParm;
    ;

    contractDataParm.initFromContractTable(contractTable);

    formClass = new BSAContractData(contractDataParm);
    formClass.populateTmpTables();

}

public int task(int _taskId)
{
    int ret;
    ;

    if(_taskId == 2839)
    {
        ret = 0; // The CTRL+F5 action messes up the temporary table
    }
    else
    {
        ret = super(_taskId);
    }

    return ret;
}

