public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (!TmpContractLineSubscription.Name)
    {
        ret = checkFailed(strFmt("@DNR8442", "@SYS7399"));
    }

    if (!TmpContractLineSubscription.SMASubscriptionGroupId)
    {
        ret = checkFailed(strFmt("@DNR8442", "@SYS84170"));
    }

    if (!TmpContractLineSubscription.ProjCategoryId)
    {
        ret = checkFailed(strFmt("@DNR8442", "@SYS84195"));
    }

    return ret;
}

