[Form]
public class BSAContractTable extends FormRun
{
    BSAContractDataParm contractDataParm;
    boolean             isCallerLinkedTable;
}

public void init()
{
    super();

    // initialize splitter
}

public void init()
{
    smmBusRelTable          busRelTable;
    RAGBusRelContractGroup  busRelContractGroup;
    WPPWorkOrderTable       workOrderTable;
    BSAContractView         contractView;
    BOBObjectTable          objectTable;

    super();

    //We want to filter this manually, so we can see the whole hierarchy
    this.queryBuildDataSource().clearDynalinks();
    this.queryBuildDataSource().clearRanges();

    switch (element.args().dataset())
    {
        case tableNum(BSAContractView):
            if (element.args().menuItemName() == menuitemDisplayStr(BSAContractTableNew))
            {
                //this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, ContractId)).value(SysQuery::DNRvalueNoMatch());
            }
            else
            {
                contractView = element.args().record();
                this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, ContractId)).value(SysQuery::value(contractView.ContractId));
            }
            break;

        case tableNum(smmBusRelTable):
            busRelTable = element.args().record();
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, BusRelRelation)).value(busRelTable.BusRelAccount);          //Table
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, BusRelRelation)).value(busRelTable.RAGBusRelGroupId);       //Group
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, BusRelRelation)).value(SysQuery::valueEmptyString());       //All
            break;

        case tableNum(RAGBusRelContractGroup):
            busRelContractGroup = element.args().record();
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, BusRelRelation)).value(busRelContractGroup.BusRelGroupId);  //Group
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, BusRelRelation)).value(SysQuery::valueEmptyString());       //All
            break;

        case tableNum(WPPWorkOrderTable):
            workOrderTable = element.args().record();
            busRelTable = smmBusRelTable::findByParty(workOrderTable.OrganizationParty); //Will always be customer
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, BusRelRelation)).value(busRelTable.BusRelAccount);          //Table
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, BusRelRelation)).value(busRelTable.RAGBusRelGroupId);       //Group
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, BusRelRelation)).value(SysQuery::valueEmptyString());       //All

            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, FromDate))      .value(SysQuery::range(dateNull(), workOrderTable.StartDate));
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, ToDate))        .value(SysQuery::range(workOrderTable.EndDate,   dateNull()));
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, CurrencyCode))  .value(SysQuery::value(busRelTable.Currency));
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, Status))        .value(SysQuery::value(BSAContractStatus::Confirmed));
            break;

        case tableNum(BOBObjectTable):
            objectTable = element.args().record();
            this.queryBuildDataSource().addRange(fieldNum(BSAContractTable, ContractId)).value(objectTable.BSADisplayActiveContractId());
            break;

    }
}

public void updateDesign()
{
    boolean         allowEditContract;

    // If this form was opened from another form, then for safety reasons you're only allowed to
    // edit/delete new contracts and contracts whose relation type corresponds to the calling record
    allowEditContract = (!isCallerLinkedTable || ContractTable.BusRelType == contractDataParm.BusRelType);

    ContractTable_ds.allowEdit(allowEditContract);
    ContractTable_ds.allowDelete(allowEditContract && ContractTable.Status == BSAContractStatus::Created);

    ContractTable_ds.object(fieldNum(BSAContractTable, BusRelRelation)).allowEdit(ContractTable.BusRelType != TableGroupAll::All);

    ContractTable_ds.object(fieldNum(BSAContractTable, ProjId)).allowEdit(ContractTable.BusRelType == TableGroupAll::Table && ContractTable.Status == BSAContractStatus::Created);
    ContractTable_ds.object(fieldNum(BSAContractTable, ProjId)).mandatory(ContractTable.BusRelType == TableGroupAll::Table && ContractTable.Status == BSAContractStatus::Created);

    ContractTable_ds.object(fieldNum(BSAContractTable, ProjInvoiceProjId)).allowEdit(ContractTable.BusRelType == TableGroupAll::Table && ContractTable.Status == BSAContractStatus::Created);
    ContractTable_ds.object(fieldNum(BSAContractTable, ProjInvoiceProjId)).mandatory(ContractTable.BusRelType == TableGroupAll::Table && ContractTable.Status == BSAContractStatus::Created);

    ProjTableButton.enabled(contractTable.BusRelType == TableGroupAll::Table);
    ProjInvoiceTableButton.enabled(contractTable.BusRelType == TableGroupAll::Table);
    ProjInvoiceControlButton.enabled(contractTable.BusRelType == TableGroupAll::Table);
    ProjTableCreate.enabled(ContractTable.BusRelType == TableGroupAll::Table && ContractTable.Status == BSAContractStatus::Created);
    Reference.enabled(ContractTable.Status == BSAContractStatus::Created);

    Project.enabled(ContractTable.BusRelType == TableGroupAll::Table);
    BSAChangeContractStatus.enabled(ContractTable.Status < BSAContractStatus::Canceled);

    BSAContractSubscription.enabled(contractTable.BusRelType == TableGroupAll::Table && contractTable.hasSubscriptions());

    MessageGroup.visible(!contractTable.IsSynchronized_MaintPlans || !contractTable.IsSynchronized_Subscription);
}

public void initValue()
{
    smmBusRelTable          busRelTable;
    RAGBusRelContractGroup  busRelContractGroup;

    super();

    // number sequence retrieval can't be done on the table since the
    // query's dynalink (if existing) will overwrite the sequence number
    ContractTable.ContractId = BSAContractTable::getNewContractId();

    if (isCallerLinkedTable)
    {
        ContractTable.BusRelType        = contractDataParm.BusRelType;
        ContractTable.BusRelRelation    = contractDataParm.getBusRelRelation();
        ContractTable.CurrencyCode      = contractDataParm.CurrencyCode;
    }
    else
    {
        ContractTable.BusRelType        = BSAParameters::tableId2CustType(element.args().dataset());

        switch (element.args().dataset())
        {
            case tableNum(smmBusRelTable):
                busRelTable                   = element.args().record();
                ContractTable.BusRelRelation  = busRelTable.BusRelAccount;
                break;

            case tableNum(RAGBusRelContractGroup):
                busRelContractGroup           = element.args().record();
                ContractTable.BusRelRelation  = busRelContractGroup.BusRelGroupId;
                break;
        }
    }
}

public int active()
{
    int ret;

    ret = super();

    ContractTable_ds.updateDesign();

    return ret;
}

public void modified()
{
    super();

    contractTable.BusRelRelation = '';
    ContractTable_ds.updateDesign();

}

void clicked()
{
    super();

    ContractTable_ds.reread();

    if (contractTable.IsSynchronized_MaintPlans && contractTable.IsSynchronized_Subscription)
    {
        info("@DNR8451");
        MessageGroup.visible(false);
    }
}

void clicked()
{
    Args args = new Args(formStr(ProjTableCreate));
    FormRun formRun;

    args.caller(element);
    args.openMode(OpenMode::New);
    args.copyCallerQuery(CopyCallerQuery::No);
    args.record(contractTable);

    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    contractTable_ds.refresh();
}

void clicked()
{
    Args        args = new Args();
    ProjTable   projTable;
    ;

    //super();

    if (contractTable.ProjId)
    {
        select firstOnly projTable
        where projTable.ProjId == contractTable.ProjId;

        args.record(projTable);
        args.caller(element);
        args.callerFormControl(this);

        new menuFunction(menuitemDisplayStr(ProjInvoiceTableCreate), MenuItemType::Display).run(args);

        contractTable_ds.refresh();
    }
    else
    {
        error("@DNR8427");
    }

}
