/// <summary>
/// Performs additional actions when the sales order is created.
/// </summary>
protected void salesTableCreated()
{
    if (isRU)
    {
        salesTable_RU.data(SysExtensionSerializerExtensionMap::findByBase(salesTable_RU.TableId,
                                                                                          salesTable.RecId,
                                                                                          true));
        salesTable_RU_DS.rereadReferenceDataSources();
    }

    if (BrazilParameters::isEnabled())
    {
        salesTable_BR.data(SysExtensionSerializerExtensionMap::findByBase(salesTable_BR.TableId,
                                                                                          salesTable.RecId,
                                                                                          true));

        salesTable_BR_DS.rereadReferenceDataSources();
    }

    if (isRU || BrazilParameters::isEnabled())
    {
        salesTable_ds.refresh();
    }

    if (isSalesTableExtensionTHEnabled)
    {
        salesTable_DS.research(true);
    }

    if (isSalesTableWEnabled)
    {
        salesTable_W.data(SysExtensionSerializerExtensionMap::findByBase(salesTable_W.TableId,
                                                                                        salesTable.RecId,
                                                                                        true));

        salesTable_W_DS.rereadReferenceDataSources();
        salesTable_DS.refresh();
    }
}

/// <summary>
/// Customizes the options used to populate the Office Menu with the sales order entities.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityOptionsIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityOptionsIterator.value();

        DictDataEntity dataEntity = new DictDataEntity(tablename2Id(dataEntityOptions.dataEntityName()));

        if (dataEntity.formRef() == menuItemDisplayStr(ReturnTableDetails))
        {
            dataEntityOptionsIterator.delete();
        }
        else
        {
            dataEntityOptionsIterator.next();
        }
    }

     // Extensibility TODO: Request pending at Microsoft - 541362
    /*if (WHSFlexibleWarehouseLevelDimensionReservationLPFeature::instance().isEnabled())
    {
        var menuItem = OfficeGeneratedExportMenuItem::construct(tableStr(WHSOrderCommittedLicensePlateReservationEntity), tableStr(WHSOrderCommittedLicensePlateReservationEntity));

        menuItem.displayName(strFmt('%1 (%2)', new SysDictTable(tableNum(WHSOrderCommittedLicensePlateReservationEntity)).label(), curExt()));

        _menuOptions.customMenuItems().addEnd(menuItem);
    }    */
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = ExportToExcelDataEntityContext::constructDefault(_menuItem.dataEntityName());

    if (_menuItem.dataEntityName() == tableStr(WHSOrderCommittedLicensePlateReservationEntity))
    {
        ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(_menuItem.dataEntityName());

        context.findEntityByName(_menuItem.dataEntityName()).filter(filterBuilder.and(filterBuilder.companyFilter(),
                            filterBuilder.and(filterBuilder.areEqual(fieldStr(WHSOrderCommittedLicensePlateReservationEntity, OrderNumber), SalesTable.SalesId),
                            filterBuilder.areEqual(fieldStr(WHSOrderCommittedLicensePlateReservationEntity, ReferenceCategory), InventTransType::Sales))));
    }

    return context;
}

