{
    // disable sales Table
    salesTable_ds.allowEdit(false);
    salesTable_ds.allowCreate(false);
    salesTable_ds.allowDelete(false);
    salesLine_ds.allowEdit(false);
    salesLine_ds.allowCreate(false);
    salesLine_ds.allowDelete(false);
}

