/// <summary>
/// Return order fields specific to India are getting updated here.
/// </summary>
public void updateReturnableDetails_IN()
{
    SalesReturnInformation_IN           salesReturnInformation;
    TaxInformation_IN                   taxInformation;
    SalesReturn_IN                      salesReturn = SalesReturn_IN::construct(salesLine.TaxItemGroup);
    SalesReturnPeriodUnit_IN            salesReturnPeriodUnit = SalesReturnPeriodUnit_IN::None;
    SalesReturnCalculationDateType_IN   salesReturnCalculationDateType = SalesReturnCalculationDateType_IN::None;
    SalesLine                           salesLineOrig;
    CustInvoiceTrans                    custInvoiceTransOrig;
    TransTaxInformation                 transTaxInformationLocal;

    transTaxInformationLocal = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(salesLine);

    taxInformation         = TaxInformation_IN::find(transTaxInformationLocal.TaxInformation);
    salesReturnInformation = salesReturn.getReturnInformation(taxInformation);

    if (salesLineReturnDetails_IN.Type != SalesReturnType_IN::None && salesReturnInformation.RecId)
    {
        salesLineReturnDetails_IN.BasisType = (str2enum(salesReturnCalculationDateType, enum2Value(salesReturnInformation.BasisType)));

        if (salesLineReturnDetails_IN.Type == SalesReturnType_IN::CustomerReturns)
        {
            salesLineReturnDetails_IN.Period     = salesReturnInformation.Period;
            salesLineReturnDetails_IN.PeriodType = (str2enum(salesReturnPeriodUnit, enum2Value(salesReturnInformation.PeriodType)));
        }
        else
        {
            salesLineReturnDetails_IN.Period     = salesReturnInformation.IncompletePeriod;
            salesLineReturnDetails_IN.PeriodType = (str2enum(salesReturnPeriodUnit, enum2Value(salesReturnInformation.IncompletePeriodType)));
        }
        if (salesLine.InventTransIdReturn)
        {
            custInvoiceTransOrig = CustInvoiceTrans::findRecId(salesLineReturnDetails_IN.CustInvoiceTrans);
            salesLineOrig        = custInvoiceTransOrig.salesLine();
            if (salesReturnInformation.BasisType == CustomerCalculationDateType_IN::InvoiceDate)
            {
                salesLineReturnDetails_IN.BasisDate = custInvoiceTransOrig.InvoiceDate;
            }
            else if (salesReturnInformation.BasisType == CustomerCalculationDateType_IN::DeliveryDate)
            {
                salesLineReturnDetails_IN.BasisDate =  salesLineOrig.ReceiptDateConfirmed ;
            }
        }
        salesLineReturnDetails_IN.SalesLine_IN  = salesLine_IN.RecId;
        salesLineReturnDetails_IN_ds.object(fieldNum(SalesLineReturnDetails_IN, BasisType)).allowEdit(true);
        salesLineReturnDetails_IN_ds.object(fieldNum(SalesLineReturnDetails_IN, Period)).allowEdit(true);
        salesLineReturnDetails_IN_ds.object(fieldNum(SalesLineReturnDetails_IN, PeriodType)).allowEdit(true);
        salesLineReturnDetails_IN_ds.write();
    }
    else
    {
        salesLineReturnDetails_IN.Period     = 0;
        salesLineReturnDetails_IN.BasisType  = SalesReturnCalculationDateType_IN::None;
        salesLineReturnDetails_IN.PeriodType = SalesReturnPeriodUnit_IN::None;
        salesLineReturnDetails_IN_ds.write();
        salesLineReturnDetails_IN_ds.object(fieldNum(SalesLineReturnDetails_IN, BasisType)).allowEdit(salesLineReturnDetails_IN.Type != SalesReturnType_IN::None);
        salesLineReturnDetails_IN_ds.object(fieldNum(SalesLineReturnDetails_IN, Period)).allowEdit(salesLineReturnDetails_IN.Type != SalesReturnType_IN::None);
        salesLineReturnDetails_IN_ds.object(fieldNum(SalesLineReturnDetails_IN, PeriodType)).allowEdit(salesLineReturnDetails_IN.Type != SalesReturnType_IN::None);
    }
}

boolean useRetailPricingEngine()
{
    return RetailPricingEngine::useRetailPricingEngineForSalesOrder(salesTable.SalesId);
}

/// <summary>
/// Record details of manual override.
/// </summary>
/// <param name="_reasonCode">
/// Reason code used to change price/discount.
/// </param>
/// <param name="_userId">
/// user id.
/// </param>
void mcrLineManualDiscountOverride(RetailInformationSubcodeId _reasonCode, UserId _userId = curUserId())
{
    if (#MCRPriceOverrideEnabled)
    {
        mcrSalesLinePriceOverride.PriceOverride = NoYes::No;
        mcrSalesLinePriceOverride.ReasonCode = _reasonCode;
        // Load these values so that the appropriate values go into the order event.
        mcrLinePriceOverrideEventType  = MCROrderEventType::SalesLinePriceOverrideTurnedOn;
        mcrLinePriceOverrideReasonCode = _reasonCode;
        mcrLinePriceOverrideUserId = _userId;
    }
}

/// <summary>
/// Enable/Disable the EDIT button according to current status of sales order
/// </summary>
public void mcrSwapCompleteAndModifyButtons()
{
    boolean completeButtonIsEnable = (salesTable.SalesStatus == SalesStatus::Backorder && salesTable.mcrOrderStopped == NoYes::Yes);

    this.design().viewEditMode(completeButtonIsEnable ? ViewEditMode::Auto : ViewEditMode::View);

    LineOverviewMainActionButtonGroup.enabled(completeButtonIsEnable);

    // Toggle edit mode of the form.
    // Do it only not in a transaction.
    if (element.inViewMode() == completeButtonIsEnable && appl.ttsLevel() ==0)
    {
        element.task(#taskEditRecord);
    }
}

/// <summary>
/// Performs lookup for <c>CustInvoiceJour</c> table.
/// </summary>
/// <param name="_control">
/// The control to require lookup.
/// </param>
/// <param name="_queryRange">
/// The range of query.
/// </param>
/// <param name="_custAccount">
/// The account of customer.
/// </param>
public void performCustInvoiceJourLookup_MY(
    FormStringControl   _control,
    Range               _queryRange,
    CustAccount         _custAccount)
{
    custInvoiceJourLookupController.parmQueryRange(_queryRange);
    custInvoiceJourLookupController.parmCustAccount(_custAccount);
    custInvoiceJourLookupController.performLookup(_control);
}

/// <summary>
///     Updates the controls used for selecting source invoice transaction.
/// </summary>
public void updateSourceRefControlsAfterLookup_MY()
{
    if (custInvoiceJourLookupController.parmInvoiceInfo())
    {
        [salesLine_W.RefInvoiceId_MY, salesLine_W.RefInvoiceDate_MY] = custInvoiceJourLookupController.parmInvoiceInfo();

        // After updated, clear the InvoiceInfo container of CustInvoiceJourLookupController to prepare for next lookup.
        custInvoiceJourLookupController.parmInvoiceInfo(conNull());
    }
}

/// <summary>
/// Refresh retail related datasources
/// </summary>
private void refreshRetailDatasource()
{
    if (isRetailOrder)
    {
        RetailSalesTable newRetailSalesTable = RetailSalesTable::findSalesTable(salesTable);
        if (newRetailSalesTable)
        {
            LoyaltyCardLineView.dataSourceObject().clearReferenceData(fieldNum(RetailSalesTable, RetailLoyaltyCard));
            RetailSalesTable.data(newRetailSalesTable);
            RetailSalesTable_ds.rereadReferenceDataSources();
            RetailSalesTable_ds.refresh();
        }
    }

    // for call center orders refresh data source
    if (mcrCallCenterOrder || mcrSalesTable)
    {
        MCRSalesTable newMCRSalesTable = MCRSalesTable::findSalesId(salesTable.SalesId);

        mcrSalesTable.data(newMCRSalesTable);
        MCRSalesTable_ds.rereadReferenceDataSources();
        MCRSalesTable_ds.refresh();
    }
}

public int task(int _taskId)
{
    #Task
    int ret;

    newButtonCurrentSalesId   = ((taskId == #taskNew) && (_taskId == #taskClearFfilter)) ? salesTable.SalesId : null;
    taskId = _taskId;

    ret = super(_taskId);
    if (mcrCallCenterEnabled &&
       (_taskId == #taskSwitchToDetailsView || _taskId == #taskSwitchToHeaderDetailsView || _taskId == #taskSwitchToLineDetailsView))
    {
        this.mcrSetFormParts();
    }

    // when restoring, if the price was reset to the original price, then the override reason is not mandatory.
    if ((_taskId ==  #taskRestore) && mcrCallCenterEnabled && enableOrderPriceControl && retailSalesLine.OriginalPrice &&
       (retailSalesLine.OriginalPrice == salesLine.SalesPrice))
    {
        RetailSalesLine_PriceOverrideReasonCode.mandatory(false);
    }

    return ret;
}

private boolean forceCompleteReturn()
{
    boolean     ret;
    SalesTable  salesTableReturn;
    SalesLine   salesLineReturn;

    if (mcrCallCenterEnabled)
    {
        // valid return order exists.
        select firstOnly RecId from salesTableReturn
            where salesTableReturn.ReturnReplacementId == salesTable.SalesId
            exists join salesLineReturn
                where salesLineReturn.SalesId == salesTableReturn.SalesId
                && salesLineReturn.ExpectedRetQty != 0;

        ret = salesTableReturn ? true : false;
    }

    return ret;
}

/// <summary>
/// Sets product value based on selected product from the lookup.
/// </summary>
public void setProductFromLookup(InventDistinctProduct _product)
{
    productFromLookup = _product;
}

/// <summary>
/// Updates product dimensions on the sales line.
/// </summary>
/// <param name="_inventDim">
/// An instance of <c>InventDim</c> that provides the new product dimensions.
/// </param>
public void updateProductDimensions(InventDim _inventDim)
{
    Set productDimensionFields = inventDim.copyProductDimensionsAndGetModifiedDimensionFields(_inventDim, salesLine.ItemId);
    if (productDimensionFields)
    {
        element.modifyInventDimSet(inventDim, productDimensionFields);
        element.performUpdatePostProductDimensionChange();
    }
}

/// <summary>
/// Default gift card details on the sales line.
/// </summary>
/// <param name="_inventDim">
/// An instance of <c>InventDim</c> that provides the new product dimensions.
/// </param>
public void defaultGiftCardDetails(InventDim _inventDim)
{
    RetailSharedParameters retailSharedParameters;
    RetailParameters retailParameters;
    RetailStoreTenderTypeTable retailStoreTenderTypeTable;

    // Only proceed if call center and advanced external gift card is enabled
    if (mcrCallCenterEnabled)
    {
        retailSharedParameters = RetailSharedParameters::find();

        if (retailSharedParameters.EnableAdvancedExternalGiftCard)
        {
            // Check to see if item is a gift card item
            select firstonly retailStoreTenderTypeTable
                where retailStoreTenderTypeTable.GiftCardItemId == salesLine.ItemId;

            if (retailStoreTenderTypeTable)
            {
                retailParameters = RetailParameters::find();

                if (retailParameters)
                {
                    // Does the gift card variant support electronic delivery
                    Set dlvModes = RetailShippingDeliveryMode::getLineDeliveryOptions(salesLine);
                    if (dlvModes.in(retailParameters.ElectronicDeliveryModeCode))
                    {
                        // Default to email
                        mcrSalesLine.GiftCardType = MCRGiftCardType::Email;
                        salesLine.DlvMode = retailParameters.ElectronicDeliveryModeCode;
                    }
                    else
                    {
                        // Default to physical
                        mcrSalesLine.GiftCardType = MCRGiftCardType::Physical;
                        salesLine.DlvMode = SalesTable.DlvMode;
                    }
                }
            }
        }
    }
}

/// <summary>
/// Performs updates needed after product dimension changes.
/// </summary>
public void performUpdatePostProductDimensionChange()
{
    if (salesLine.DeliveryDateControlType)
    {
        SalesCalcAvailableDlvDates salesCalcAvailableDlvDates = SalesCalcAvailableDlvDates::newCommonSalesDlvDateType(salesLine, 0, inventDim);
        salesCalcAvailableDlvDates.modifiedFieldBeforeInsert(salesLine);
    }

    salesLine_DS.cacheCalculateMethod(tableMethodStr(SalesLine,itemName));

    element.mcrRefreshMarginAlert();

    element.findProductNumber();
}

/// <summary>
/// Returns the related <c>PurchTable</c> record for this <c>SalesTable</c> taking the line selection into account.
/// If the <c>SalesLine</c> record does not have a referenced <c>PurchTable</c> then the referenced <c>PurchTable</c> record of the <c>SalesTable</c> is returned.
/// </summary>
/// <param name = "_intercompany">If the related <c>PurchTable</c> record must be part of an intercompany chain.</param>
/// <returns>The related <c>PurchTable</c> record for this <c>SalesTable</c> taking the line selection into account.</returns>
private PurchTable referencedPurchTableConsideringLineSelection(boolean _intercompany)
{
    if (!_intercompany || SalesLine.isIntercompanyOrigin())
    {
        PurchLine referencedPurchLine = salesLine.referencedPurchLine();
        if (referencedPurchLine)
        {
            return referencedPurchLine.purchTable();
        }
    }

    return salesTable.referencePurchTable();
}

/// <summary>
/// Refresh inventory dimension related datasources
/// </summary>
private void refreshInventDimDatasource()
{
    InventDim.data(SalesLine.inventDim());
    InventDim_DS.setCurrent();
}

public void modifyInventDimField(FieldId _fieldId)
{
    if (salesLine.modifyInventDim(inventDim, _fieldId, !salesLine.MatchingAgreementLine))
    {
        element.refreshInventDimDatasource();
    }
}

/// <summary>
/// Updates <c>InventDim</c> data based on the given set of modified dimension fields.
/// </summary>
/// <param name = "_inventDim">The new inventory dimension record.</param>
/// <param name = "_dimFields">A set containing the ID's of the modified <c>InventDim</c> fields.</param>
public void modifyInventDimSet(InventDim _inventDim, Set _dimFields)
{
    if (salesLine.modifyInventDimSet(_inventDim, _dimFields, !salesLine.MatchingAgreementLine))
    {
        element.refreshInventDimDatasource();
    }
}

/// <summary>
/// Updates source identifier on sales table header to sales lines.
/// </summary>
public void mcrUpdateSourceIdToSalesLines()
{
    SalesLine               salesLineLocal;

    MCRSalesTable.update();

    // Unable to replace by a set based update because there are extensions consuming salesLine.mcrUpdateSourceId()
    while select forUpdate salesLineLocal
            where salesLineLocal.SalesId == SalesTable.SalesId
    {
        salesLineLocal.mcrUpdateSourceId(mcrSalesTable.SourceId);
    }

    element.recalculateRetailPricesDiscounts();
}

/// <summary>
/// Check if workflow action button will be displayed.
/// </summary>
/// <param name = "_workflowType">
/// Workflow type.
/// </param>
/// <returns>
/// Return true if any line needs to submit and not submitted.
/// </returns>
public boolean canSubmitToWorkflow(str _workflowType = '')
{
    boolean ret;
    MCRSalesLine mcrSalesLineLocal;
    SalesLine salesLineLocal;

    if (mcrSalesLine)
    {
        // if current line is not submitted or approved
        if (mcrSalesLine.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::NotSubmitted || mcrSalesLine.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::Approved || mcrSalesLine.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::None)
        {
            // and if any non submit line is need to submit for approval
            select firstonly RecId from mcrSalesLineLocal
                join RecId from salesLineLocal
                    where mcrSalesLineLocal.SalesLine == salesLineLocal.RecId && salesLineLocal.SalesId == salesTable.SalesId
                        && mcrSalesLineLocal.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::NotSubmitted;

            if (mcrSalesLineLocal)
            {
                ret = true;
            }

        }
    }

    // hide workflow action button even if the line is rejected and the line's status is None and no not submitted lines
    element.design().controlName("WorkflowActionBarButtonGroup").visible(ret || mcrSalesLine.RetailPriceOverrideWorkflowState != RetailPriceOverrideWorkflowState::None);

    return ret;
}

/// <summary>
/// Updates the linked datasources to the sales table datasource.
/// </summary>
protected void salesTableLinkActiveBasedOnDatasource()
{
    ProjTable projTableLink;
    QueryBuildRange projIdSalesTable;

    switch (element.args().dataset())
    {
        case tableNum(CostControlTransCommittedCost):
            CostControlTransCommittedCost costControlTransCommittedCost =  element.args().record() as CostControlTransCommittedCost;
            queryBuildRangeSalesId.value(queryValue(costControlTransCommittedCost.RefId));
            break;

        case tableNum(ProjTable)        :
            projTableLink = element.args().record() as ProjTable;
            queryBuildRangeProjId.value(projTableLink.ProjId+'*');
            break;
        case tableNum(ProjInvoiceTable):
            projIdSalesTable    = salesTable_ds.query().dataSourceTable(tableNum(SalesTable)).findRange(fieldNum(SalesTable,ProjId));

            while (projIdSalesTable)
            {
                salesTable_ds.query().dataSourceTable(tableNum(SalesTable)).clearRange(fieldNum(SalesTable,ProjId));
                projIdSalesTable  = salesTable_ds.query().dataSourceTable(tableNum(SalesTable)).findRange(fieldNum(SalesTable,ProjId));
            }

            ProjInvoiceTable projInvoiceTableLink   = element.args().record() as ProjInvoiceTable;

            while select ProjId from projTableLink
                    where projTableLink.ProjInvoiceProjId   == projInvoiceTableLink.ProjInvoiceProjId
            {
                salesTable_ds.query().dataSourceTable(tableNum(SalesTable)).addRange(fieldNum(SalesTable,ProjId)).value(projTableLink.ProjId);
            }
            break;

        case tableNum(ProjWIPTable)     :
            projIdSalesTable    = salesTable_ds.query().dataSourceTable(tableNum(SalesTable)).findRange(fieldNum(SalesTable,ProjId));

            while (projIdSalesTable)
            {
                salesTable_ds.query().dataSourceTable(tableNum(SalesTable)).clearRange(fieldNum(SalesTable,ProjId));
                projIdSalesTable  = salesTable_ds.query().dataSourceTable(tableNum(SalesTable)).findRange(fieldNum(SalesTable,ProjId));
            }

            ProjWIPTable projWIPTable   =   element.args().record() as ProjWIPTable;

            while select ProjId from projTableLink
                    where projTableLink.wipProject  == projWIPTable.ProjId
            {
                salesTable_ds.query().dataSourceTable(tableNum(SalesTable)).addRange(fieldNum(SalesTable,ProjId)).value(projTableLink.ProjId);
            }

            break;

        case tableNum(smmActivities) :
            smmActivityParentLink::updateQuery(salestable_ds.query().dataSourceTable(tableNum(SalesTable)), element.args().record() as smmActivities);
            break;
            // If deduction has salesId specified, show that sales order, otherwise
            // show all sales orders with invoice account equal to deduction's account
        case tableNum(TAMDeduction)     :
            // Clear ranges
            salesTable_ds.query().dataSourceTable(tableNum(SalesTable)).
                        clearRange(fieldNum(SalesTable, InvoiceAccount));
            salesTable_ds.query().dataSourceTable(tableNum(SalesTable)).
                        clearRange(fieldNum(SalesTable, SalesId));

            TAMDeduction tamDeduction;
            tamDeduction.data(element.args().record());
            if (tamDeduction.SalesId)
            {
                // If deduction has SalesId just show that sales order
                salesTable_ds.query().dataSourceTable(tableNum(SalesTable))
                            .addRange(fieldNum(SalesTable, SalesId)).value(tamDeduction.SalesId);
            }
            else
            {
                // If no SalesId specified show all sales orders with invoice
                // account equal to deduction's account
                salesTable_ds.query().dataSourceTable(tableNum(SalesTable))
                            .addRange(fieldNum(SalesTable, InvoiceAccount)).value(tamDeduction.AccountNum);
            }
            break;
    }
}

/// <summary>
/// Rereads the salesLine record from db and refreshes the margin alert cache.
/// </summary>
/// <remarks>
/// This method will be called from an external class.
/// </remarks>
public void mcrRereadMarginAlert()
{
    RecVersion versionBeforeUpdate = salesLine.RecVersion;
    salesLine_ds.reread();
    if (salesLine.RecVersion != versionBeforeUpdate)
    {
        element.mcrRefreshMarginAlert();
        salesLine_ds.refresh();
    }
}

