/// <summary>
///     Initializing <c>TmpPurchLinePrice</c> table buffer from <c>SalesLine</c> table record.
/// </summary>
/// <param name = "_tmpPurchLinePrice">
///     A <c>TmpPurchLinePrice</c> table buffer.
/// </param>
/// <param name = "_salesLine">
///     A <c>SalesLine</c> table buffer.
/// </param>
protected void initializeTmpPurchLinePriceFromSalesLine(TmpPurchLinePrice _tmpPurchLinePrice)
{
    _tmpPurchLinePrice.AccountNum        = salesLine.inventTable().PrimaryVendorId;
    _tmpPurchLinePrice.SalesId           = salesLine.SalesId;
    _tmpPurchLinePrice.ItemId            = salesLine.ItemId;
    _tmpPurchLinePrice.InventDimId       = salesLine.InventDimId;
    _tmpPurchLinePrice.SalesLineRefRecId = salesLine.RecId;
    _tmpPurchLinePrice.Included          = NoYes::Yes;
}

/// <summary>
///     Determines whether the caller form is valid for creating new sales order.
/// </summary>
/// <returns>
///     true if caller form is not allowed create new sales order; otherwise, false.
/// </returns>
protected boolean mustCreateSalesOrder()
{
    // If we are called from CustomerService and we already have
    // created a Sales Order do NOT allow ctrl-n.
    if (salesTable.SalesId && mcrCustomerServiceParm)
    {
        return true;
    }

    // Refactored logic to run do not allow open order logic in a separate method.
    // Need to know whether or not the new create was allowed, if it is not
    // return out of the create function.
    if (!element.mcrCheckOpenOrder())
    {
        return true;
    }

    return false;
}

/// <summary>
///     Creates new sales orders.
/// </summary>
protected void createSalesOrder()
{

    salesTable.insert();
}

