{
    element.editSales(salesTableForm.editHeaderAllowed(), salesTableForm.deleteHeaderAllowed());
    element.mcrSetHeaderControls();
    element.mcrSetLineControls();
}

public int first()
{
    int ret;

    // Selects the first record on the sales table.  This is not allowed if the user has
    // opened an order and open orders are not allowed due to the order completion process being enabled.
    if (enableOrderCompletion
        && openOrderSalesTable)
    {
        return ret;
    }

    ret = super();

    return ret;
}

public int last()
{
    int ret;

    // Selects the last record on the sales table.  This is not allowed if the user has
    // opened an order and open orders are not allowed.
    if (enableOrderCompletion
        && openOrderSalesTable)
    {
        return ret;
    }
    ret = super();

    return ret;
}

void reReadLines()
{
    salesLine_ds.executeQuery();
}

public boolean leave()
{
    boolean ret;

    ret = super();

    // Prevent leave method from firing on a delete
    if (deleting)
    {
        return ret;
    }
    salesTableForm.interCompanyAutoCreateOrders();

    // if the status of the order being evaluated has had
    // its status changed to something other than open order
    // (backorder) clear out the buffer which indicates an open order.
    if (enableOrderCompletion)
    {
        element.mcrCheckOpenOrder();

        salesTable_ds.reread();
    }

    return ret;
}

public void showDimAccount()
{
    boolean NOEnabled = salesTable.ProjId && CustTable::isCustPublic_NO(SalesTable.InvoiceAccount);

    eInvoice_EInvoiceLineSpec.visible(!NOEnabled);
    eInvoice_EInvoiceAccountCode.enabled(NOEnabled || !salesTable.eInvoiceLineSpec);
    eInvoiceLine_EInvoiceAccountCode.enabled(NOEnabled || salesTable.eInvoiceLineSpec);
    eInvoiceLine_OrderLineReference_NO.enabled(NOEnabled || salesTable.eInvoiceLineSpec);
}

void checkCreditLimit()
{
    SalesUpdate qtySpec = element.salesUpdateFromTypeOfCreditmaxCheck();

    SalesTotals salesTotals = SalesTotals::construct(salesTable, qtySpec);

    salesTable.checkCreditLimit(salesTotals);
}

