/// <summary>
/// Deletes the related direct delivery purchase order.
/// </summary>
/// <param name = "_mcrDropShipmentOrigValue">The original <c>MCRDropShipment</c> value retrieved from the orig buffer.</c></param>
/// <remarks>
/// This is used for cases when user un-checks the drop shipment flag.
/// </remarks>
protected void deleteDirectDeliveryPurchaseOrder(MCRDropShipment _mcrDropShipmentOrigValue = NoYes::No)
{
    if (salesLine
        && mcrSalesLineDropShipment
        && !mcrSalesLineDropShipment.DropShipment
        && _mcrDropShipmentOrigValue)
    {
        MCRSalesLineDropShipment::deleteRelatedPurchaseOrderLine(salesLine.RecId);
        salesLine_ds.reread();
    }
}

/// <summary>
///     Returns the margin percent value and margin icon for the sale line.
/// </summary>
/// <param name="_salesLine">
///     A buffer of the sales line record selected on the form.
/// </param>
/// <returns>
///     The margin percent, icon and textual status.
/// </returns>
private container mcrGetMarginValues(SalesLine _salesLine)
{
    if (salesParameters.mcrEnableMarginAlert
        && _salesLine.InventTransId)
    {
        // Use the value in the map for performance reasons if exists
        if (marginMap.exists(_salesLine.InventTransId))
        {
            return marginMap.lookup(_salesLine.InventTransId);
        }
        else
        {
            str marginIcon;
            MCRMarginPecentStr marginPecentStr = _salesLine.mcrDisplayMarginPercentStr();

            // If the margin could be calculated then determine the icon
            if (_salesLine.McrMarginPercent != McrMarginAlert::percentValueForNotCalculated())
            {
                marginIcon = MCRMarginAlert::getMarginSymbol(_salesLine.McrMarginPercent);
            }
            marginMap.insert(_salesLine.InventTransId, [marginPecentStr, marginIcon]);

            return [marginPecentStr, marginIcon];
        }
    }

    return conNull();
}

/// <summary>
///     Displays the margin percent value for the sale line.
/// </summary>
/// <param name="_salesLine">
///     A buffer of the sales line record selected on the form.
/// </param>
/// <returns>
///     The margin percent.
/// </returns>
display MCRMarginPecentStr mcrMarginPercentStr(SalesLine _salesLine)
{
    container marginAlertValues = this.mcrGetMarginValues(_salesLine);
    return conPeek(marginAlertValues, 1);
}

/// <summary>
///     Displays the margin icon value for the sale line.
/// </summary>
/// <param name="_salesLine">
///     A buffer of the sales line record selected on the form.
/// </param>
/// <returns>
///     The margin icon.
/// </returns>
display container mcrMarginIcon(SalesLine _salesLine)
{
    SymbolFontGlyphName symbol = conPeek(this.mcrGetMarginValues(_salesLine), 2);
    return ImageReference::constructForSymbol(symbol).pack();
}

/// <summary>
/// Updates the InventLocationId and InventSiteId of inventDim if the Drop shipment flag is set
/// </summary>
private void updateInventDimForDropShipment()
{
    MCRInventTable mcrInventTableLocal = MCRInventTable::findByItemId(salesLine.ItemId);

    if (mcrInventTableLocal.DropShipment)
    {
        InventTableModule inventTableModule = InventTableModule::find(salesLine.ItemId,ModuleInventPurchSales::Sales);
        inventDim.InventLocationId = inventTableModule.inventLocationId();
        inventDim.InventSiteId = inventTableModule.inventSiteId();

        salesLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;
        salesLine.modifyInventDim(inventDim,fieldNum(InventDim,InventLocationId));
    }
}

/// <summary>
/// If retail order, discount is recalculated after deleting line.
/// </summary>
/// <param name="_salesLine">
/// Sales line record on the grid
/// </param>
private void retailDiscountRecalculate(SalesLine _salesLine)
{
    RetailSalesTable           retailSales = salesTable.retailSalesTable();

    if (RetailParameters::isRetailEnabledAndInUse() &&
        RetailPricingEngine::useRetailPricing(_salesLine))
    {
        RetailSalesOrderCalculator::setPricesDiscountsOnOrderOnServer(salesTable.SalesId, retailSales.TotalManualDiscountPercentage);
        salesLine_ds.research(true);
    }
}

public void deleteMarked()
{
    mcrOrderLine2PriceHistoryRefRecId = salesLine.MCROrderLine2PriceHistoryRef;

    // fails to compile on 10.0.14
    /*if (kittingFormHandler)
    {
        kittingFormHandler.startMultiSelectDeletion();
    }*/

    inDeleteMarked = true;
    super();
    inDeleteMarked = false;

    this.retailDiscountReCalculate(salesLine);

    // fails to compile on 10.0.14
    /*if (kittingFormHandler)
    {
        kittingFormHandler.endMultiSelectDeletion(this);
    }*/
}

/// <summary>
/// If quantity is changed on a sales line, and the line has a linked (not direct delivery, not intercompany) purchase order line, prompts user to determine if
/// linked purchase order line should also be updated.
/// </summary>
/// <returns>true if the linked purchase order should be updated; otherwise, false.</returns>
private boolean updateLinkedPurchaseOrderLineOnQuantityUpdate()
{
    if (salesLine.InventRefTransId
    && salesLine.InventRefType == InventRefType::Purch
    && salesLine.DeliveryType == TradeLineDlvType::None
    && salesLine.SalesQty != salesLine.orig().SalesQty
    && !PurchTable::find(salesLine.InventRefId).InterCompanyOrder)  // For intercompany orders we always sync the quantities, so don't ask.
    {
        PurchLine purchLine = PurchLine::findInventTransId(salesLine.InventRefTransId);

        if (Box::yesNo(strFmt("@SCM:SalesLineQtyChangeUpdateLinkedPurchLine", purchLine.PurchId, purchLine.LineNumber), DialogButton::Yes))
        {
            return true;
        }
    }

    return false;
}

/// <summary>
///	Check if current line need to be marked as "need to submit for approval".
/// </summary>
public void mcrSetWorkflowApprovalStatus()
{
    boolean                         currentUserPermission;
    real                            newPrice;
    real                            marginPrice;
    AmountCur                       brokerages;
    AmountCur                       rebates;
    AmountCur                       royalties;
    AmountCur                       totalDeductible;
    MCRMarginAlert_Sales            mcrMarginAlert_Sales;
    MCROverrideLogin_OverridePrice  mcrOverrideLogin_OverridePrice;
    WorkflowWorkItemTable			workItem;

    mcrOverrideLogin_OverridePrice = new MCROverrideLogin_OverridePrice();

    mcrMarginAlert_Sales = MCRMarginAlert_Sales::newSalesLine(salesLine);
    mcrMarginAlert_Sales.calcMargin();
    brokerages      = mcrMarginAlert_Sales.parmBrokerAmountPerUnit();
    rebates         = mcrMarginAlert_Sales.parmCustRebateAmountPerUnit();
    royalties       = mcrMarginAlert_Sales.parmRoyaltyAmountPerUnit();
    totalDeductible = brokerages + rebates + royalties;

    newPrice = 100 * mcrOverrideLogin_OverridePrice.calculateCostMarkupPercent(
                salesLine.ItemId,
                salesLine.InventDimId,
                salesLine.SalesUnit,
                salesLine.mcrPricePerSalesQty());
    marginPrice = 100 * mcrOverrideLogin_OverridePrice.calculateMarginCostMarkupPercent(
                salesLine.ItemId,
                salesLine.InventDimId,
                salesLine.SalesUnit,
                salesLine.mcrPricePerSalesQty(),
                totalDeductible);

    currentUserPermission = mcrOverrideLogin_OverridePrice.isUserValid(curUserId(),
                                                                        newPrice,
                                                                        fieldNum(MCRPriceOverrideMarkUpGroup, CostMarkUpPercent),
                                                                        marginPrice);

    if (!currentUserPermission)
    {
        if (mcrSalesLine.RetailPriceOverrideWorkflowState != RetailPriceOverrideWorkflowState::Rejected)
        {
            // cannot use element.getActiveWorkflowWorkItem() because if canSubmitToWorkflow return true, it will return null
            select firstonly workItem
                where workItem.DocumentClassId == classNum(RetailSalesLineWFTypeDocument)
                    && workItem.Status == WorkflowWorkItemStatus::Pending
                    && workItem.Type == WorkflowWorkItemType::Return
                    && workItem.RefTableId == tableNum(SalesLine)
                    && workItem.RefRecId == salesLine.RecId;

            if (workItem)
            {
                mcrSalesLine.RetailPriceOverrideWorkflowState = RetailPriceOverrideWorkflowState::Rejected;
            }
            else
            {
                mcrSalesLine.RetailPriceOverrideWorkflowState = RetailPriceOverrideWorkflowState::NotSubmitted;
            }
        }
    }
    else
    {
        mcrSalesLine.RetailPriceOverrideWorkflowState = RetailPriceOverrideWorkflowState::None;
    }

    RetailSalesLine_PriceOverrideReasonCode.mandatory(mcrSalesLine.RetailPriceOverrideWorkflowState != RetailPriceOverrideWorkflowState::None);
}

