}

/// <summary>
/// If an agreement is associated with the SO then reset the price since it won't be reset after the matching agreement line is found.
/// </summary>
protected container mustResetPriceOnItemIdModified()
{
    return salesLine.itemIdChanged(salesTable.MatchingAgreement != 0, salesTable.MatchingAgreement, true /* perform product variant validation */);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    HierarchyTreeTable::lookupHierarchyTree(_formControl, ProjTable::find(salesLine.ProjId));
}

public void modified()
{
    super();
    salesLine_ds.object(fieldNum(SalesLine, Samples_MX)).enabled(salesLine.Consignment_MX);
}

public boolean validate()
{
    boolean ret;
    CustTable custTableLocal;

    ret = super();
    if (mcrCallCenterEnabled)
    {
        custTableLocal = CustTable::find(salesTable.CustAccount);
        // Do not allow a user to manually enter a phantom custAccount.
        if (custTableLocal.mcrMergedRoot)
        {
            checkFailed(strFmt("@MCR12337", salesTable.CustAccount, custTableLocal.mcrMergedRoot));
            // Return the parent account number
            salesTable.CustAccount = custTableLocal.mcrMergedRoot;
        }
    }

    return ret;
}

