public void lookup(FormControl _formControl, str _filterStr)
{
    if (mcrCallCenterEnabled && salesLine.mcrSalesLine().SourceId != "")
    {
        Catalog::lookupItemsFromSourceId(salesLine.mcrSalesLine().SourceId, _formControl, _filterStr);
    }
    else
    {
        if (EcoResProductParameters::find().pcLookupMethod == PCLookupMethod::Configurable)
        {
            Args args = new Args(formStr(PCProductLookup));
            args.caller(element);
            FormRun formRun = classfactory.formRunClass(args);
            formRun.init();
            this.performFormLookup(formRun, _formControl);
        }
        else if (isProductSearchLookupEnabled)
        {
            EcoResProductSearchLookup::lookupProduct(_formControl as FormStringControl);
        }
        else
        {
            SalesLine::lookupItemNumber(_formControl, SalesLine, _filterStr);
        }
    }
}

void  modified()
{
    ItemId itemId = salesLine.ItemId;
    salesTableForm.resetSalesLine(salesLine);
    element.setItemIdFieldsOnSalesLine(itemId);
    if (productFromLookup )
  //      && SalesTableMCRInventSearchBeforeSetItemBarcodeFlight::instance().isEnabled()) // if there was product selected from the product lookup
    {
        salesLine.type().setStockedProduct();
        element.updateProductDimensions(InventDim::find(productFromLookup.InventDimId));
        productFromLookup = null;
    }

    element.setItemBarcodeFieldsOnSalesLine();

    boolean isDropShipment = SalesLine.mcrInventTable().DropShipment;
    if (isDropShipment)
    {
        element.defaultInventDimBasedOnDirectDeliveryWarehouse(isDropShipment);
    }

    super();

    // fails to compile on 10.0.14
    /*if (kittingFormHandler && kittingFormHandler.isKit())
    {
        salesLine.salesPurchLine::setPriceAgreement(salesLine.inventDim(), true, null, true);
    }*/

    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        if (salesLine.orig().ItemId != '')
        {
            TransTaxInformation transTaxInformationLocal = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(salesLine);
            if (transTaxInformationLocal)
            {
                ttsbegin;
                transTaxInformationLocal.selectForUpdate(true);
                TransTaxInformationHelper::initFromInventItem(transTaxInformationLocal, salesLine.ItemId, CustTable::find(salesTable.CustAccount));
                transTaxInformationLocal.update();
                ttscommit;
            }
        }
    }

    // <GIN>
    if (isIndiaIndirectTaxParameterMarked)
    {
        if (SalesTable::find(salesLine.SalesId).CustomsExportOrder_IN == NoYes::Yes)
        {
            //MRP defaulted from item is converted using Customs exchange rate for Export orders.
            salesLine_IN.MaximumRetailPrice = CurrencyHelper_IN::customsCurAmount_IN(salesLine_IN.CustomsMaxRetailPrice, salesTable.CurrencyCode, Direction_IN::Export, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), false);
        }
    }
    if (TaxParameters::isCustomsEnable_IN()
        && salesTable.CustomsExportOrder_IN
        && TaxParameters::find().EximIncentiveSchemes_IN)
    {
        eximSalesLine_IN.EximProductGroupTable = InventTable::find(salesLine.ItemId).EximProductGroupTable_IN;
        element.enableFieldsActive_IN();
    }
    if (TaxParameters::checkTaxParameters_IN())
    {
        salesLine_IN.assessableValue(salesLine);
    }
    if (TaxParameters::checkTaxParameters_IN() || TaxWithholdParameters_IN::checkTaxParameters())
    {
        salesLine_IN_ds.refresh();
    }

    // </GIN>

    salesLine_ds.refreshDDC();

    salesLine_DS.cacheCalculateMethod(tableMethodStr(SalesLine,itemName));
    if (isConfigurationkeyEnabled(configurationKeyNum(PDS)))
    {
        salesLine_ds.cacheCalculateMethod(tableMethodStr(SalesLine,pdsCWUnitId));
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        salesLine.setCFOP_BR();

        if (salesLine.inventTable().ItemType != ItemType::Service)
        {
            salesLine.LedgerDimension = SalesPurchOperationType_BR::find(salesTable_BR.SalesPurchOperationType_BR).LedgerDimension;
        }

        salesLine_ds.refresh();

        salesLine_BR_DS.enableDataSourceFields_BR();
    }
    // </GBR>

    if (mcrCallCenterEnabled)
    {
        element.mcrDisplayItemMessage();
        MCRSalesLine_ds.giftCardPolicyChanged();
        element.mcrSetLineControls();
    }

    if (productFromLookup) // if there was product selected from the product lookup
    {
        element.updateProductDimensions(InventDim::find(productFromLookup.InventDimId));
        productFromLookup = null;
    }

    // set original price when item is entered or changed
    if (mcrCallCenterEnabled && mcrCallCenterOrder && enableOrderPriceControl)
    {
        retailSalesLine.OriginalPrice = salesLine.SalesPrice;
    }

    // fails to compile on 10.0.14
    /*if (kittingFormHandler && kittingFormHandler.isKit())
    {
        kittingFormHandler.setLineAccess(SalesLine_DS, InventDim_DS);
    }*/
}

