public int active()
{
    int     ret;
    boolean allowEditLine;

    ret = super();

    if (salesLine.isCategoryBased())
    {
        allowEditLine = salesTableForm.editLineAllowed();
        salesLineForeignTradeCategory_ds.allowEdit(allowEditLine);
    }
    else
    {
        salesLineForeignTradeCategory_ds.allowEdit(false);
    }

    return ret;
}

