public void lookup(FormControl _formControl, str _filterStr)
{
    if (!EcoResProductMasterConfigurationMgr::configure(_formControl,
                                                       salesLine.ItemId,
                                                       InventDim::findOrCreate(inventDim).InventDimId,
                                                       salesLine.deliveryDate(),
                                                       salesLine.ItemBOMId,
                                                       salesLine.QtyOrdered))
    {
        super(_formControl, _filterStr);
    }
}

public boolean validate()
{
    boolean ret = super();

    if (ret)
    {
        ret = salesLine.validateModifyInventDim(inventDim,fieldNum(InventDim,ConfigId));
    }

    return ret;
}

public void modified()
{
    SalesCalcAvailableDlvDates salesCalcAvailableDlvDates;
    boolean resetPriceInterCompany = true;

    super();

    // When Sales order is Intercompany and intercompany setup exists
    // (Purchase order policy setup has Price search set to True and Sales order policy has Price search set to False)
    // we don't have to recalculate the price.  
    if (SalesTable.isInterCompanyOrder()
        && !SalesTable.interCompanyEndpointActionPolicy().PriceDiscountSearch
        &&  SalesTable.interCompanyPurchTable().interCompanyEndpointActionPolicy().PriceDiscountSearch)
       // &&  SalesInterCompanyPriceRecalculationFlight::instance().isEnabled())
    {
        resetPriceInterCompany = false;
    }


    salesLine.modifyInventDim(inventDim, fieldNum(InventDim,InventBatchId), !salesLine.MatchingAgreementLine && resetPriceInterCompany);

    if (salesLine.DeliveryDateControlType)
    {
        salesCalcAvailableDlvDates =  SalesCalcAvailableDlvDates::newCommonSalesDlvDateType(salesLine,0,inventDim);
        salesCalcAvailableDlvDates.modifiedFieldBeforeInsert(salesLine);
    }

    element.mcrRefreshMarginAlert();
}

