{
    //super(_append);
}

public boolean validate()
{
    boolean ret = super();

    if (ret && mcrCallCenterEnabled && enableOrderPriceControl)
    {
        ret = salesLine_ds.mcrGetOverrideResponse(fieldNum(RetailSalesLine, LineManualDiscountPercentage),
                                                retailSalesLine.LineManualDiscountPercentage,
                                                fieldNum(MCRPriceOverrideMarkUpGroup, DiscPercentOverride));
    }

    return ret;
}

public void modified()
{
    // Remove cached line as discount is not getting applied if user inputs a manual discount percent that is same as cache.
    RetailSalesOrderCalculatorCache::removeCachedLine(SalesLine);

    super();

    salesLine.setPriceDisc(inventDim, true);

    element.mcrRefreshMarginAlert();

    if (mcrCallCenterEnabled && enableOrderPriceControl)
    {
        salesLine_ds.mcrSetWorkflowApprovalStatus();
    }
}

/// <summary>
/// Populates the lookup for the reason code field.
/// </summary>
/// <param name = "_formControl">
/// The control to require lookup.
/// </param>
/// <param name = "_filterStr">
/// The string to filter lookup data.
/// </param>
public void lookup(FormControl _formControl, str _filterStr)
{
    super(_formControl, _filterStr);
    RetailMCRChannelTable::lookupPriceOverrideReasonCodes(_formControl);
}

/// <summary>
/// Update the mandatory property to false when the sales price reverts back to original price.
/// </summary>
public void modified()
{
    super();

    if (mcrCallCenterEnabled && enableOrderPriceControl && retailSalesLine.OriginalPrice &&
       (retailSalesLine.OriginalPrice == salesLine.SalesPrice))
    {
        RetailSalesLine_PriceOverrideReasonCode.mandatory(false);
    }
}

public void write()
{
    if (salesLine_W.SalesLine)
    {
        // no-op because a record with an empty FK is not valid
        super();

        // <GMY>
        if (isIsoMY)
        {
            if (SalesTable.hasLinesWithRefrence_MY() && SalesTable.hasLinesWithoutRefrence_MY())
            {
                if (!isWrnPrintedMY)
                {
                    warning("@SYP4860678");
                    isWrnPrintedMY = true;
                }
            }
            else
            {
                isWrnPrintedMY = false;
            }
        }
        // <GMY>
    }
}

public void init()
{
    isSalesLineWEnabled = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(SalesLine_W));
    if (isSalesLineWEnabled)
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();

    if (isSalesLineWEnabled)
    {
        element.formRunHelper().addExtensionTablePair(salesLine, salesLine_W);
    }
}

public boolean validateWrite()
{
    boolean         ret;

    if (SalesLine_W.SalesLine)
    {
        ret = super();
    }
    else
    {
        ret = true;
    }

    // <GMY>
    if (ret && isIsoMY)
    {
        if (SalesLine_W.RefInvoiceId_MY && SalesLine_W.RefInvoiceDate_MY)
        {
            if (!CustInvoiceJourLookupController_MY::validateRefInvoiceInfo([SalesLine_W.RefInvoiceId_MY, SalesLine_W.RefInvoiceDate_MY, RefNum::SalesOrder]))
            {
                ret = checkFailed("@SYP4860680");
            }
        }
        else if ((SalesLine_W.RefInvoiceId_MY && !SalesLine_W.RefInvoiceDate_MY) || (!SalesLine_W.RefInvoiceId_MY && SalesLine_W.RefInvoiceDate_MY))
        {
            ret = checkFailed("@SYP4860689");
        }
    }
    // <GMY>

    return ret;
}

public void init()
{
    super();

    element.formRunHelper().addExtensionTablePair(salesTable, RetailSalesTable);
}

public void write()
{
    //Updates to RetailSalesTable should be done by the packed extension.
    if (isRetailOrder)
    {
        salesTable.packRetailSalesTable(RetailSalesTable);
        super();
    }
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret && RetailSalesTable.RetailLoyaltyCard)
    {
        ret = RetailLoyaltyCard::AllowToUse(SalesTable.CustAccount, RetailSalesTable.RetailLoyaltyCard);
    }

    return ret;
}

