    // Disable create so non-Retail orders won't try to insert an invalid row in RetailSalesTable
    // super(_append);
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    // Build SysReferenceTableLookup object with the proper fields.
    SysReferenceTableLookup sysRefTableLookup = SysReferenceTableLookup::newParameters(tableNum(RetailLoyaltyCard), _formReferenceControl);
    sysRefTableLookup.addLookupfield(fieldNum(RetailLoyaltyCard, CardNumber));
    sysRefTableLookup.addLookupfield(fieldNum(RetailLoyaltyCard, Party));
    sysRefTableLookup.addLookupfield(fieldNum(RetailLoyaltyCard, CardTenderType));

    // Build a query.
    Query lookupQuery = new Query();
    QueryBuildDataSource lookupQueryDataSource = lookupQuery.addDataSource(tableNum(RetailLoyaltyCard));

    // Display the cards owned by the customer and the anonymous cards.
    RefRecId customerParty = CustTable::find(SalesTable.CustAccount).Party;
    if (customerParty)
    {
        lookupQueryDataSource.addRange(fieldNum(RetailLoyaltyCard, Party)).value(queryValue(customerParty));
    }
    lookupQueryDataSource.addRange(fieldNum(RetailLoyaltyCard, Party)).value(queryValue(0));

    // Sort to put the owned cards on the top and then the anonymous cards
    lookupQueryDataSource.addOrderByField(fieldNum(RetailLoyaltyCard, Party), SortOrder::Descending);
    lookupQueryDataSource.addOrderByField(fieldNum(RetailLoyaltyCard, CardNumber), SortOrder::Ascending);

    sysRefTableLookup.parmQuery(lookupQuery);
    return sysRefTableLookup.performFormLookup();
}

