void clicked()
{
    if (!SalesTable.validateAllLinesDelivery())
    {
        return;
    }
    else
    {
        // Update percent amount of previous authorized credit card payments to zero
        MCRCustPaymTable custPaymTable;
        update_recordset custPaymTable
        setting
            PercentAmount = 0.0
        where custPaymTable.RefRecId == salesTable.RecId
            && custPaymTable.RefTableId == tablenum(SalesTable)
            && custPaymTable.PercentAmount != 0.0
            && custPaymTable.CustPaymType == MCRCustPaymType::CreditCard
            && custPaymTable.Status != MCRCustPaymStatus::NotSubmitted;

        //Do not call super as logic is needed before and after call.
        //Also need SalesTable form to lock while order summary is open.
        //super();
        if (SalesTable.mcrValidateGiftItem())
        {
            if (element.forceCompleteReturn())
            {
                // return order
                element.mcrLaunchOrderSummaryForReturnExchange();
                element.doRefresh();
            }
            else
            {
                // normal order
                element.mcrLaunchOrderSummary();
                salesTable_ds.reread();
                element.mcrSwapCompleteAndModifyButtons();
                // Sends email to the respected person when sales order gets completed.
                RetailEventNotificationAction::InsertRetailOENAction(RetailEventNotificationType::OrderConfirmation, SalesTable.RecId, SalesTable);
            }

            // The active method does a lot of enabling and disabling as well as calls set header control.
            salesTable_ds.active();
        }
    }
}

