/// <summary>
/// Resets the fulfillment status of a rejected line.
/// </summary>
public void clicked()
{
    // Update the fulfillment line status if the order is rejected
    if (retailSalesLine && 
        (retailSalesLine.FulfillmentStatus == RetailFulfillmentLineStatus::Rejected
            || retailSalesLine.FulfillmentStatus == RetailFulfillmentLineStatus::Pending
            || retailSalesLine.FulfillmentStatus == RetailFulfillmentLineStatus::Accepted))
    {
        RetailTransactionServiceFulfillment::resetFulfillmentStatusFromEditSalesLine(salesLine);
        element.doRefresh();
    }
}

