Method.Source:
[Form]
public class LBAContractTable extends FormRun
{
    [SysObsolete("Variable parmObjectTable is no longer used", false)]
    BOBObjectTable parmObjectTable;

}

[SuppressBPWarningAttribute("BPCheckInvalidInitFormMethodWarning", " validation should be performd before invoking super()")] 
public void init()
{
    super();

    FINDimValueController::setEditableDimensionSet(tableNum(LBAContractTable), DimensionEntryControl);
}

public void updateDesign()
{
    boolean enabled = (contractTable.ContractStatus != LBAContractStatus::Running && contractTable.ContractStatus != LBAContractStatus::Ended);

    ContractTable_ds.allowEdit(enabled);
    ContractTable_ds.allowDelete(enabled);

    ContractTerm_ds.allowCreate(enabled);
    ContractTerm_ds.allowEdit(enabled);
    ContractTerm_ds.allowDelete(enabled);

    ImportTerms.enabled(contractTable.ContractSTatus == LBAContractStatus::Created);
    UpdateTerms.enabled(contractTable.ContractStatus == LBAContractStatus::TermsImported || contractTable.ContractStatus == LBAContractStatus::Running);
    StartContract.enabled(contractTable.ContractStatus == LBAContractStatus::TermsImported);
    StopContract.enabled(contractTable.ContractStatus == LBAContractStatus::Running && contractTable.Stopped == NoYes::No);
    TerminateContract.enabled(contractTable.ContractStatus == LBAContractStatus::Running && contractTable.Stopped == NoYes::Yes);

    element.setContractType();
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    if (objectTable)
    {
        qbds = this.query().addDataSource(tableNum(LBAContractObject));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.relations(false);
        qbds.addRange(fieldNum(LBAContractObject, BOBObjectId)).value(objectTable.ObjectId);
    }
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign();

    return ret;
}

void clicked()
{
    if (contractTable.ContractStatus == LBAContractStatus::TermsImported)
    {
        if (Box::okCancel("@DNR6899",DialogButton::Cancel) == DialogButton::Ok)
        {
            ttsBegin;
            contractTable.validateTermAmounts();
            ttsCommit;

            contractTable_ds.reread();
            contractTable_ds.refresh();
        }
    }
}

void clicked()
{
    if (contractTable.ContractStatus == LBAContractStatus::Running && contractTable.Stopped == NoYes::No)
    {
        if (Box::okCancel('@DNR6875',DialogButton::Cancel) == DialogButton::Ok)
        {
            ttsBegin;
            contractTable.Stopped = NoYes::Yes;
            contractTable.update();
            ttsCommit;

            contractTable_ds.reread();
            contractTable_ds.refresh();
        }
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.setContractType();
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.getObjectTable();
    }

    return ret;
}
