boolean canClose()
{
    boolean datesOK = true;

    if (element.closedOk())
    {
        datesOK = isConfigurationkeyEnabled(configurationKeyNum(RORRentalDateTimes)) && RORRentalDateTimes.validateWrite();
    }

    if (purchTableType && !element.closedOk())
    {
        purchTableType.formMethodClose();
    }

    if (element.closedOk() 
        && (!purchTable_ds.validateWrite() 
            || (vendorName.allowEdit() && !vendorName.validate())
            || !datesOK))
    {
        return false;
    }

    purchTable_ds.write();

    return true;
}

void changeType(PurchaseType _purchaseType)
{
    if (purchTableType)
    {
        numberSeqFormHandlerPurchId = purchTableType.parmNumberSeqFormHandlerPurchId();
    }

    purchCreateOrderForm = PurchCreateOrderForm::construct(_purchaseType, purchTableForm.project());

    if (purchTableForm.project())
    {
        purchTableType = new PurchTableType_Project(purchTable);
    }
    else
    {
        purchTableType       = purchTable.type();
    }

    purchTableType.parmNumberSeqFormHandlerPurchId(numberSeqFormHandlerPurchId);
}

/// <summary>
/// Configs the Bank Document Controls.
/// </summary>
public void configBankDocumentControls()
{
    boolean enableDocumentType = false;

    if (bankLCImportFeatureChecker.checkFeatureEnabled()
        || bankLGFeatureChecker.checkFeatureEnabled())
    {
        enableDocumentType = purchTable.type().mayBankDocumentTypeBeModified();
        purchTable_DS.object(fieldNum(PurchTable, BankDocumentType)).enabled(enableDocumentType);
    }
}

private void removePurchAgreementLink()
{
    if (purchTable.OrderAccount)
    {
        if (transferCurrencyAndDeliveryAddressFromAgreement)
        {
            purchTable.initFromVendTable(VendTable::find(purchTable.OrderAccount));
        }
        transferCurrencyAndDeliveryAddressFromAgreement = false;
        purchTable.MatchingAgreement = 0;
    }
}

private boolean canPurchAgreementBeLinked(PurchAgreementId _purchAgreementId)
{
    if (!_purchAgreementId)
    {
        return false;
    }

    PurchAgreementHeader purchAgreementHeader = PurchAgreementHeader::findAgreementId(_purchAgreementId);

    if (!purchAgreementHeader)
    {
        checkFailed(strFmt("@SYS112224",
                        _purchAgreementId, "@SYS309959", tableId2pname(tableNum(PurchAgreementHeader))));
        return false;
    }

    if ((purchTable.OrderAccount
        && purchAgreementHeader.VendAccount
        && purchTable.OrderAccount != purchAgreementHeader.VendAccount)||
        (purchTable.InvoiceAccount
        && purchAgreementHeader.purchAgreementHeaderDefault().VendorInvoiceAccount
        && purchAgreementHeader.purchAgreementHeaderDefault().VendorInvoiceAccount != purchTable.InvoiceAccount))
    {
        checkFailed(strFmt("@GLS115616"));
        return false;
    }

    return true;
}

//BP Deviation Documented
edit PurchAgreementId editPurchAgreementId(boolean _set, PurchAgreementId _purchAgreementId)
{
    PurchAgreementHeader    purchAgreementHeader;
    AgreementHeaderDefault  agreementHeaderDefault;
    #ISOCountryRegionCodes

    if (_set)
    {
        if (_purchAgreementId != purchAgreementId)
        {
            purchAgreementHeader = PurchAgreementHeader::findAgreementId(_purchAgreementId);

            if (purchAgreementHeader.RecId)
            {
                if (!this.canPurchAgreementBeLinked(_purchAgreementId))
                {
                    this.removePurchAgreementLink();
                    return purchAgreementId;
                }

                agreementHeaderDefault = AgreementHeaderDefault::findAgreementHeader(purchAgreementHeader.RecId);
                if (purchTable.ProjId
                    && agreementHeaderDefault.Project
                    && purchTable.ProjId != agreementHeaderDefault.Project)
                {
                    info("@SYS334900");
                    return purchAgreementId;
                }

                if (purchAgreementHeader.canReleaseAgreement(purchTable.DeliveryDate))
                {
                    transferCurrencyAndDeliveryAddressFromAgreement = true;
                    purchAgreementId = _purchAgreementId;
                    if (Box::yesNo("@SYS311926",DialogButton::Yes) == DialogButton::Yes)
                    {
                        purchTable.CurrencyCode = purchAgreementHeader.Currency;
                        purchTable.LanguageId = purchAgreementHeader.Language;

                        purchTable.initFromPurchAgreementHeader(purchAgreementHeader);

                        purchTable_CurrencyCode.update();
                        purchTable_ds.refresh();
                    }
                    else
                    if (countryRegion_RU)
                    {
                        purchTable.DefaultDimension = purchTable.copyDimension(purchAgreementHeader.DefaultDimension);
                    }
                }
                else
                {
                    transferCurrencyAndDeliveryAddressFromAgreement = false;
                    throw error("@SYS103075");
                }
            }
            else if (!_purchAgreementId)
            {
                purchAgreementId = _purchAgreementId;
                transferCurrencyAndDeliveryAddressFromAgreement = false;
                if (countryRegion_RU)
                {
                    purchTable.DefaultDimension = AgreementHeaderExt_RU::clearAgreementDimension(purchTable.DefaultDimension);
                }
            }
        }
    }

    return purchAgreementId;
}

