public void create(boolean _append = false)
{
    super(_append);

    if (createRentalDateTimes)
    {
        RORRentalDateTimes.initRentalDateTimes(PurchTable);

        if (purchTableForm.RORParmSelectionFromDateTime())
        {
            RORRentalDateTimes.OnRentDateTime = DateTimeUtil::removeTimeZoneOffset(purchTableForm.RORParmSelectionFromDateTime(), DateTimeUtil::getUserPreferredTimeZone());
            RORRentalDateTimes.modifiedField(fieldNum(RORRentalDateTimes, OnRentDateTime));
        }

        if (purchTableForm.RORParmSelectionToDateTime())
        {

            RORRentalDateTimes.OffRentDateTime = DateTimeUtil::removeTimeZoneOffset(purchTableForm.RORParmSelectionToDateTime(), DateTimeUtil::getUserPreferredTimeZone());
            if (RORRentalDateTimes.validateField(fieldNum(RORRentalDateTimes, OffRentDateTime)))
            {
                RORRentalDateTimes.modifiedField(fieldNum(RORRentalDateTimes, OffRentDateTime));
            }
            else
            {
                warning("@DNR8596");
            }
        }

        RORRentalDateTimes.populateNonSqlFields();

        createRentalDateTimes = false;
    }
}

