/// <summary>
/// Updates the total discount.
/// </summary>
void clicked()
{
    if(PurchParameters::find().AutomaticTotalDiscount)
    {
        purchaseTable.updateFinalDisc();

        purchaseTable_ds.reread();
        purchaseTable_ds.refresh();
    }

    PurchTotals::showTax(menuitemDisplayStr(TaxTmpWorkPurchOrder), purchaseTable);
}

void  clicked()
{
    PurchTable localPurchTable;

    super();

    if(PurchParameters::find().AutomaticTotalDiscount)
    {
        for (localPurchTable = purchaseTable_ds.getFirst(true) ? purchaseTable_ds.getFirst(true) : purchaseTable_ds.cursor(); localPurchTable; localPurchTable = purchaseTable_ds.getNext())
        {
            localPurchTable.updateFinalDisc();
        }
        purchaseTable_ds.reread();
        purchaseTable_ds.refresh();
    }
    purchaseTable.updateMultiLineDisc();
}

/// <summary>
/// Opens the related taskpage to edit a record.
/// </summary>
void clicked()
{
    super();

    if (purchaseTable.PurchaseType == PurchaseType::ReturnItem)
    {
        TradeInterCompany::construct(purchaseTable).formRun(formStr(ReturnTable),element);
    }
    else
    {
        TradeInterCompany::construct(purchaseTable).formRun(formStr(SalesTable),element);
    }
}

/// <summary>
/// Opens the related taskpage to edit a record.
/// </summary>
void  clicked()
{
    super();

    TradeInterCompany::construct(purchaseTable).formRun(formStr(CustPackingSlipJournal));
}

