/// <summary>
/// Opens the related taskpage to edit a record.
/// </summary>
void  clicked()
{
    if (!purchaseTable.RecId && !purchaseTable.InterCompanySalesId)
        throw(error("@SYS28047"));

    TradeInterCompany::construct(purchaseTable).menuFunction(classStr(SalesFormLetter_PickingList),MenuItemType::Action,element);
}

/// <summary>
/// Sets up the context for print management
/// </summary>
void clicked()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();


    if (purchaseTable_ds.cursor() == null)
        return;

    super();

    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Purch, PrintMgmtNodeType::PurchTable);
    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(purchaseTable_ds.cursor());
    setupContext.parmCountryRegionId(LogisticsPostalAddress::findRecId(purchaseTable.DeliveryPostalAddress).CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}
