Method.Source:
[Form]
public class RORRentalInventItemIdLookup extends FormRun
{
    FormStringControl   selectedControl;
    FormStringControl   nameAliasControl;
    FormStringControl   callerControl;
    FormDataSource      selectedFormDataSource;
    RORRentalLine       rentalLine;
    Map                 tab2ComboBoxItemMap;
    str                 filterStr;

    #define.SalesPurch(1)
    #define.Rental(2)

}

public void init()
{
    rentalLine = element.args().record();

    if (rentalLine.TableId != tableNum(SalesLine)
    &&  rentalLine.TableId != tableNum(PurchLine)
    &&  rentalLine.TableId != tableNum(SalesQuotationLine)
    &&  rentalLine.TableId != tableNum(InventTransferLine))
    {
        throw error(Error::missingRecord(formStr(RORRentalInventItemIdLookup)));
    }

    filterStr = element.args().parm();

    super();
}

public void run()
{
    boolean filterLookup    = false;
    SalesQuotationLine  salesQuotationLine;

    callerControl = SysTableLookup::getCallerStringControl(element.args());

    CtrlTab.tab(rentalLine.TradeType == RORTradeType::SalesPurch ? #SalesPurch : #Rental);

    if (rentalLine.TableId == tableNum(SalesQuotationLine))
    {
        salesQuotationLine = rentalLine;
        if(salesQuotationLine.RORProjTransType == RORProjTransType::Rental)
        {
            CtrlTab.tab(#Rental);
        }
    }

    element.updateDesign();

    filterLookup = SysTableLookup::filterLookupPreRun_2DS(callerControl,
                                                          selectedControl,
                                                          InventTable_SalesPurch_ds,
                                                          InventTable_RentalSubRental_ds);

    super();

    if (filterStr)
    {
        InventTable_RentalSubRental_ItemId.filter(filterStr);
        inventTable_SalesPurch_ItemId.filter(filterStr);
    }

    tab2ComboBoxItemMap = SysLookup::tab2ComboBox(CtrlTab, switchView);

    if (rentalLine.TradeType != RORTradeType::SalesPurch)
    {
        RentalSubRental.activatePage();
        switchView.selectText(RentalSubRental.caption());
    }


    SysTableLookup::filterLookupPostRun(filterLookup,
        callerControl.text(),
        selectedControl,
        selectedFormDataSource,
        new FormControlAmbiguousReferenceResolver(
            callerControl,
            AbsoluteFieldBinding::construct(
                fieldStr(InventTable, ItemId),
                tableStr(InventTable)),
            AbsoluteFieldBinding::construct(
                fieldStr(InventTable, NameAlias),
                tableStr(InventTable))),
        nameAliasControl,
        true);           

}

public void setSelectMode()
{    
    void setSelectedControlAndDataSource(FormStringControl  _selectedControl,
                                         FormDataSource     _selectedFormDataSource)
    {
        selectedControl         = _selectedControl;
        selectedFormDataSource  = _selectedFormDataSource;
    }

    switch (ctrlTab.tab())
    {
        case #SalesPurch  :
            this.selectMode(inventTable_SalesPurch_ItemId);
            inventTable_SalesPurch_ItemId.setFocus();
            setSelectedControlAndDataSource(inventTable_SalesPurch_ItemId, InventTable_SalesPurch_ds);
            nameAliasControl = inventTable_SalesPurch_NameAlias;
            break;

        case #Rental  :
            this.selectMode(InventTable_RentalSubRental_ItemId);
            InventTable_RentalSubRental_ItemId.setFocus();
            setSelectedControlAndDataSource(InventTable_RentalSubRental_ItemId, InventTable_RentalSubRental_ds);
            nameAliasControl = inventTable_RentalSubRental_NameAlias;
            break;
    }
}

private void updateDesign()
{
    SalesPurch.caption(rentalLine.TableId == tableNum(PurchLine) ? "@SYS53872" : "@SYS37101");
    RentalSubRental.caption(rentalLine.TableId == tableNum(PurchLine) ? "@DNR4151" : "@DNR5038");

    ctrlTab.showTabs(false);

    element.setSelectMode();
}

public void init()
{
    QueryBuildDataSource    qbdsInventTable;

    super();

    qbdsInventTable = this.queryBuildDataSource();
    qbdsInventTable.clearDynalinks();
    qbdsInventTable.addRange(fieldNum(InventTable, RORItemTradeType)).value(SysQuery::valueNot(RORItemTradeType::RentalSubRental));

    if(rentalLine.TableId == tableNum(InventTransferLine))
    {
        qbdsInventTable.addRange(fieldNum(InventTable, ItemType)).value(InventItemType::valueCanBeTransferred());
    }
}

public void init()
{
    QueryBuildDataSource qbdsInventTable;

    super();

    qbdsInventTable = this.queryBuildDataSource();
    qbdsInventTable.clearDynalinks();
    qbdsInventTable.addRange(fieldNum(InventTable, RORItemTradeType)).value(SysQuery::valueNot(RORItemTradeType::SalesPurch));

    if(rentalLine.TableId == tableNum(InventTransferLine))
    {
        qbdsInventTable.addRange(fieldNum(InventTable, ItemType)).value(InventItemType::valueCanBeTransferred());
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    ctrlTab.tabChanged(ctrlTab.tabValue(), tab2ComboBoxItemMap.lookup(this.selection()));

    return ret;
}

public void tabChanged(int _fromTab, int _toTab)
{
    super(_fromTab, _toTab);

    element.updateDesign();

    SysTableLookup::lookupTabChanged(callerControl,
                                     selectedControl,
                                     selectedFormDataSource);

}
