[Form]
public class RORRentalLineEditorV2 extends FormRun
{
    RORRentalLineEditorFormV2           rentalLineEditorV2;
    RORRentalLine                       callerRentalLine;
    boolean                             doCreateRentalLine;
    RecId                               treeSelectedRecId;
    ImageListAppl_RORRentalLine         imageList;
    boolean                             lineAmountModified;
    boolean                             mustRefreshTree;
    boolean                             parentUpdatedFromChild;
    Map                                 expandedRecords;
    RORTmpExpandedRentalEditorLines     tmpExpandedLines;
    ParmId                              parmId;
    RAGParameters                       parameters;

}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (element.closedOk())
    {
        ret = rentalLineEditorV2.checkCanCloseForm(RentalEditorHeader) && ret;
    }

    return ret;
}

public void closeOk()
{
    List rentalEditorLineList = new List(Types::Record);
    RORRentalEditorLine rentalEditorLineLocal;
    RORHeaderId         headerId = rentalLineEditorV2.getCallerRentalLine().HeaderId;

    while select rentalEditorLineLocal
    order by LineNum
    where rentalEditorLineLocal.ParmId == parmId
    && rentalEditorLineLocal.HeaderId == headerId
    && rentalEditorLineLocal.Include == NoYes::Yes
    {
        rentalEditorLineList.addEnd(rentalEditorLineLocal);
    }

    rentalLineEditorV2.parmPackedRentalEditorLineList(rentalEditorLineList.pack());

    super();
}

[SuppressBPWarningAttribute("BPCheckInvalidInitFormMethodWarning", "Validation of caller must be performed before invoking super()")]
public void init()
{
    object caller = element.args().caller();
    boolean showEffectivityDates;

    if (caller && SysDictClass::isEqualOrSuperclass(classIdGet(caller), classNum(RORRentalLineEditorFormV2)))
    {
        rentalLineEditorV2 = caller;
        parmId = rentalLineEditorV2.parmId();
        callerRentalLine = rentalLineEditorV2.getCallerRentalLine();
        expandedRecords = rentalLineEditorV2.parmExpandedRecordsMap();
        if (!expandedRecords.exists(rentalLineEditorV2.getRecIdMainKit()))
        {
            expandedRecords.insert(rentalLineEditorV2.getRecIdMainKit(), 0);
            rentalLineEditorV2.parmExpandedRecordsMap(expandedRecords);
            this.insertExpandedLine(rentalLineEditorV2.getRecIdMainKit());
        }

        parameters = RAGParameters::find();

        super();

        imageList = new ImageListAppl_RORRentalLine();
        lineIcon.imageList(imageList.imageList());
        treeControl.setImagelist(imageList.imageList());

        mustRefreshTree = true;
        element.populateTreeControl();

        showEffectivityDates = (rentalLineEditorV2.parmEditMode() == RORRentalLineEditMode::Modify && callerRentalLine.TableId != tableNum(SalesQuotationLine));
        RentalEditorHeader_ds.object(fieldNum(RORRentalEditorHeader, EffectiveDateTimeFrom)).visible(showEffectivityDates);
        RentalEditorHeader_ds.object(fieldNum(RORRentalEditorHeader, EffectiveDateTimeTo)).visible(showEffectivityDates);

        RentalEditorLine_IsPriceCalculated.visible(parameters.SkipRAGCalcForOtionalRLEComponents);
        RefreshLines.visible(parameters.SkipRAGCalcForOtionalRLEComponents);
    }
    else
    {
        throw error(strFmt("@SYS86720", element.name(), classStr(RORRentalLineEditorFormV2)));
    }
}

public void insertExpandedLine(RecId _LineRecId = 0,RecId _ParentLineRecId = 0)
{        
    rentalLineEditorV2.insertExpandedLine(_LineRecId, _ParentLineRecId);
    tmpExpandedLines = rentalLineEditorV2.parmTMPExpandedRentalEditorLines();
    RORTmpExpandedRentalEditorLines.linkPhysicalTableInstance(tmpExpandedLines);
}

public void deleteLine(RecId _DeleteRecId)
{
    delete_from tmpExpandedLines
    where tmpExpandedLines.ParmId == parmId
    &&    tmpExpandedLines.LineRecId == _DeleteRecId;

    RORTmpExpandedRentalEditorLines.linkPhysicalTableInstance(tmpExpandedLines);
}

public void deleteFromMap(RecId _LineRecId)
{
    MapEnumerator   ExpandedRecordsMapEnumerator;
    Set             expandedChildRecords = new Set(Types::Int64);

    expandedRecords.remove(_LineRecId);
    this.deleteLine(_LineRecId);
    ExpandedRecordsMapEnumerator = expandedRecords.getEnumerator();
    while (ExpandedRecordsMapEnumerator.moveNext())
    {
        if (ExpandedRecordsMapEnumerator.currentValue() == _LineRecId)
        {
            expandedChildRecords.add(ExpandedRecordsMapEnumerator.currentKey());
            expandedRecords.remove(ExpandedRecordsMapEnumerator.currentKey());
            this.deleteLine(ExpandedRecordsMapEnumerator.currentKey());
            ExpandedRecordsMapEnumerator = expandedRecords.getEnumerator();                
        }
    }
        this.deleteHierarchy(expandedChildRecords);
}

public void deleteHierarchy(Set _RecordSet)
{
    SetEnumerator   expandedChildRecordsSetEnumerator = _RecordSet.getEnumerator();
    MapEnumerator   ExpandedRecordsMapEnumerator;
    Set             tempRecordsSet = new Set(Types::Int64);

    if (!expandedRecords.empty())
    {
        while (expandedChildRecordsSetEnumerator.moveNext())
        {
            ExpandedRecordsMapEnumerator = expandedRecords.getEnumerator();
            while (ExpandedRecordsMapEnumerator.moveNext())
            {
                if (ExpandedRecordsMapEnumerator.currentValue() == expandedChildRecordsSetEnumerator.current())
                {
                    tempRecordsSet.add(ExpandedRecordsMapEnumerator.currentKey());
                    expandedRecords.remove(ExpandedRecordsMapEnumerator.currentKey());
                    this.deleteLine(ExpandedRecordsMapEnumerator.currentKey());
                    ExpandedRecordsMapEnumerator = expandedRecords.getEnumerator();
                }
            }                
            this.deleteLine(expandedChildRecordsSetEnumerator.current());
            _RecordSet.remove(expandedChildRecordsSetEnumerator.current());
            expandedChildRecordsSetEnumerator = _RecordSet.getEnumerator();
        }
        if (!tempRecordsSet.empty())
        {
            this.deleteHierarchy(tempRecordsSet);
        }
    }
}

public void populateTreeControl()
{
    RORRentalEditorLine         editorLine, childLine;
    TreeItemIdx                 treeItemIdx;
    Map                         structureMap;
    Set                         itemIdxToToggle = new Set(Types::Integer);
    SetEnumerator               setEnumerator;
    boolean                     firstElement = true;
    ;

    if(mustRefreshTree)
    {
        treeControl.deleteAll();

        // create root item
        treeItemIdx = SysFormTreeControl::addTreeItem(treeControl,
                                                      callerRentalLine.HeaderId,
                                                      FormTreeAdd::Root,
                                                      callerRentalLine.RecId,
                                                      imageList.image(imageList.imageNoOrderHeader()),
                                                      true);

        SysFormTreeControl::setOverlayImage(treeControl, treeItemIdx, imageList.image(imageList.imageNoRedLock()));

        structureMap = new Map(Types::Int64, Types::Integer); // [ RecId, TreeItemIdx ]
        structureMap.insert(0, treeItemIdx); // add entry for root node

        while select RecId, ItemId, KitComponentType, LineType, ParentLineRecId, Include from editorLine
        group by editorLine.RecId, editorLine.ItemId, editorLine.KitComponentType, editorLine.LineType, editorLine.ParentLineRecId, editorLine.Include
        where editorLine.ParmId == parmId
        && editorLine.MainKitReference == callerRentalLine.RecId
        &&    editorLine.RefTableId == callerRentalLine.TableId
            outer join count(RecId) from childLine
            where childLine.ParentLineRecId == editorLine.RecId
        {
            treeItemIdx = SysFormTreeControl::addTreeItem(treeControl,
                                                          editorLine.ItemId,
                                                          structureMap.lookup(editorLine.ParentLineRecId),
                                                          editorLine.RecId,
                                                          imageList.image(imageList.getImageNoFromRentalLine(editorLine)),
                                                         (childLine.RecId > 0));

            if (!editorLine.Include && childLine.RecId > 0)
            {
                itemIdxToToggle.add(treeItemIdx);
            }

            structureMap.insert(editorLine.RecId, treeItemIdx);

            if(editorLine.ItemId == callerRentalLine.ItemId && !editorLine.ParentLineRecId)
            {
                treeControl.select(treeItemIdx);
            }
        }

        SysFormTreeControl::expandTree(treeControl, treeControl.getRoot());

        // toggle not included branches
        setEnumerator = itemIdxToToggle.getEnumerator();
        while (setEnumerator.moveNext())
        {
            treeControl.expand(setEnumerator.current(), FormTreeExpand::Toggle);
        }
        mustRefreshTree = false;
    }
}

