public void create(boolean _append = true)
{
    if (doCreateRentalLine)
    {
        RORRentalEditorLine rentalEditorLineLocal, selectedLine;
        // Move the mouse pointer to the last record in the data source,
        // so new records/lines will be created at the bottom of the grid.

        selectedLine = this.cursor();

        select firstonly rentalEditorLineLocal
        order by LineNum desc
        where rentalEditorLineLocal.ParmId == parmId
        && rentalEditorLineLocal.ParentLineRecId == selectedLine.RecId;

        if (rentalEditorLineLocal)
        {
            this.positionToRecord(rentalEditorLineLocal);
        }

        super(_append);
    }
    else
    {
        doCreateRentalLine = true;
        rentalLineEditorV2.addManualComponent(this);
        doCreateRentalLine = false;

        mustRefreshTree = true;
    }
}

public void initValue()
{
    super();

    RentalEditorLine.CurrencyCode          = callerRentalLine.CurrencyCode;
    RentalEditorLine.PriceTimeUnit         = callerRentalLine.PriceTimeUnit;
    RentalEditorLine.ParentLineRecId       = rentalLineEditorV2.getRecIdMainKit();
    RentalEditorLine.Include               = NoYes::Yes;

    rentalLineEditorV2.initFromCallerHeader(RentalEditorLine);

    RentalEditorLine.ManualCreated         = NoYes::Yes;
}

void updateDesign(boolean   _allowEditRentalPricing,
                  boolean   _allowEditSalesPricing,
                  boolean   _allowEditQty)
{
    ItemId itemIdWarranty = RAGParameters::find().ItemIdWarranty;
    boolean canEditProfile = _allowEditRentalPricing && (!RentalEditorLine.ParentLineRecId || RentalEditorLine.KitComponentType == RORKitComponentType::Standalone);
    boolean allowEditRentalContract = RentalEditorLine.TradeType != RORTradeType::SalesPurch && RentalEditorLine.ItemId != itemIdWarranty;
    ;

    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, Include)).allowEdit(RentalEditorLine.KitRequiredLevel != RORKitRequiredLevel::Mandatory);

    RentalEditorLine_ds.object(fieldNum(RORRentalEditorLine, RentalGrossAmount)).allowEdit(_allowEditRentalPricing
        || (RentalEditorLine.LineType != RORLineType::Normal && RORRentalEditorLine::findRecId(RentalEditorLine.ParentLineRecId).KitComponentType == RORKitComponentType::Standalone));
    RentalEditorLine_ds.object(fieldNum(RORRentalEditorLine, RentalPrice)).enabled(_allowEditSalesPricing);

    // Only allow invoice profile related changes from parent lines (will then cascade)
    RentalEditorLine_ds.object(fieldNum(RORRentalEditorLine, DNRCalendarId)).allowEdit(RentalEditorLine.TradeType != RORTradeType::SalesPurch);
    RentalEditorLine_ds.object(fieldNum(RORRentalEditorLine, INVInvoiceProfileId)).allowEdit(RentalEditorLine.TradeType != RORTradeType::SalesPurch);
    RentalEditorLine_ds.object(fieldNum(RORRentalEditorLine, PriceTimeUnit)).allowEdit(RentalEditorLine.TradeType != RORTradeType::SalesPurch);
    RentalEditorLine_ds.object(fieldNum(RORRentalEditorLine, INVRentalModeId)).allowEdit(RentalEditorLine.TradeType != RORTradeType::SalesPurch);
    RentalEditorLine_ds.object(fieldNum(RORRentalEditorLine, INVRateMultiplierTier)).allowEdit(_allowEditRentalPricing || _allowEditSalesPricing);

    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, RentalQty)).allowEdit(_allowEditQty);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, RentalUnit)).allowEdit(_allowEditQty);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, QtyOrdered)).allowEdit(_allowEditQty);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, RentalPrice)).allowEdit(_allowEditSalesPricing);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, PriceUnit)).allowEdit(_allowEditSalesPricing);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, LineAmount)).allowEdit(_allowEditSalesPricing);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, Markup)).allowEdit(_allowEditSalesPricing);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, LineDisc)).allowEdit(_allowEditSalesPricing);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, LinePercent)).allowEdit(_allowEditSalesPricing);

    RentalEditorLine_Overview_M_editNetAmount.allowEdit(RentalEditorLine.ItemId != itemIdWarranty && !RentalEditorLine.IsPriceFixed);
    RentalEditorLine_Overview_M_editLineAmount.allowEdit(RentalEditorLine.ItemId != itemIdWarranty && !RentalEditorLine.IsPriceFixed);
    LinesGrid_M_editNetAmount.allowEdit(RentalEditorLine.ItemId != itemIdWarranty && !RentalEditorLine.IsPriceFixed);
    LinesGrid_M_editLineAmount.allowEdit(RentalEditorLine.ItemId != itemIdWarranty && !RentalEditorLine.IsPriceFixed);
    PriceGroup_M_editNetAmount.allowEdit(RentalEditorLine.ItemId != itemIdWarranty && !RentalEditorLine.IsPriceFixed);

    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, InsuranceCalcBasis)).allowEdit(allowEditRentalContract);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, InsurancePercentage)).allowEdit(allowEditRentalContract);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, InsureRentalStops)).allowEdit(allowEditRentalContract);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, TransportPrice)).allowEdit(allowEditRentalContract);
    RentalEditorLine_ds.object(fieldNum(RORRentalEditorLine, ReturnTransportPrice)).allowEdit(allowEditRentalContract);
    RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, WarrantyPrice)).allowEdit(allowEditRentalContract);

    // It is not allowed to delete a record from the RentalEditorLine
    AdditionalLineNotes.visible(callerRentalLine.TableId != tableNum(PurchLine));
    AdditionalLineNotes.enabled(RentalEditorLine.TradeType != RORTradeType::SalesPurch);

    // Copy line is only allowed when the calling rental line is not the same
    // as the current rental editor line
    CopyLine.enabled(callerRentalLine.TableId != RentalEditorLine.RefTableId || callerRentalLine.RecId != RentalEditorLine.RefRecId);

    // Check if the parameter is enabled and if the selected RLE line is not mandatory then only enable the include button when the price is calculated
    // this is done for the performance optimization to open the RLE form open faster as the pricing calc is time consuming and allowing 'Include' button can cause update conflicts
    if (parameters.SkipRAGCalcForOtionalRLEComponents && RentalEditorLine.KitRequiredLevel != RORKitRequiredLevel::Mandatory)
    {
        RentalEditorLine_ds.object(fieldnum(RORRentalEditorLine, Include)).allowEdit(RentalEditorLine.IsPriceCalculated);
    }

}

public int active()
{
    int     ret;
    boolean allowEditRentalPricing;
    boolean allowEditSalesPricing;
    boolean allowEditQty;

    ret = super();

    [allowEditRentalPricing, allowEditSalesPricing, allowEditQty] = RentalEditorLine.RORRentalLine::CheckAllowEdit();

    this.updateDesign(allowEditRentalPricing, allowEditSalesPricing, allowEditQty);

    rentalEditorLine_ds.allowCreate(rentalEditorLine.Include
        && (rentalEditorLine.KitComponentType == RORKitComponentType::Kit || rentalEditorLine.KitComponentType == RORKitComponentType::KitComponent || rentalEditorLine.LineNum == 1));

    RentalEditorLine_ds.allowDelete(RentalEditorLine.ManualCreated);

    return ret;
}

public void write()
{
    boolean                 updateKitPricing;
    boolean                 lineInserted;
    RORKitPricingActionType kitPricingActionType;
    boolean                 forceUpdateFromComponent;
    InventTable             inventTable;
    boolean                 childLinesUpdated;
    RORRentalEditorLine     rentalEditorLineParent;
    Price                   newPrice;
    SalesLineDisc           lineDisc;
    boolean                 setNewPriceOnContract;
    RAGParameters           ragParameters = RAGParameters::find();
    ItemId                  itemId;
    ;

    if ((RentalEditorLine.orig().RentalGrossAmount != RentalEditorLine.RentalGrossAmount ||
         RentalEditorLine.orig().LinePercent != RentalEditorLine.LinePercent ||
         RentalEditorLine.orig().Include != RentalEditorLine.Include)
    &&  RentalEditorLine.LineType != RORLineType::Normal)
    {
        rentalEditorLineParent = RORRentalEditorLine::findRecId(RentalEditorLine.ParentLineRecId);

        if (rentalEditorLineParent && rentalEditorLineParent.KitComponentType == RORKitComponentType::Standalone)
        {
            if (RentalEditorLine.Include)
            {
                if (RentalEditorLine.LineType == RORLineType::Insurance)
                {
                    newPrice = (rentalEditorLineParent.RentalGrossAmount) ? RentalEditorLine.RentalGrossAmount / rentalEditorLineParent.RentalGrossAmount * 100 : 0;
                }
                else
                {
                    newPrice = RentalEditorLine.RentalGrossAmount;
                }

                lineDisc = RentalEditorLine.LinePercent;
            }

            setNewPriceOnContract = true;
            itemId = RentalEditorLine.ItemId;
        }
    }

    lineInserted = !RentalEditorLine.RecId || (RentalEditorLine.Include && RentalEditorLine.orig().Include != RentalEditorLine.Include);
    forceUpdateFromComponent = RentalEditorLine.orig().Include != RentalEditorLine.Include;
    updateKitPricing = (RentalEditorLine.orig().Include != RentalEditorLine.Include ||
                        RentalEditorLine.RORRentalLine::canUpdateKitPricing() ||
                        RentalEditorLine.rentalContractElementsChanged());

    childLinesUpdated = RentalEditorLine.cascadeToLines();

    super();

    if (updateKitPricing)
    {
        if (lineInserted)
        {
            kitPricingActionType = RORKitPricingActionType::CreateLines;

            rentalLineEditorV2.processRentalEditorLineRAGContractData(rentalEditorLine);
        }
        else
        {
            kitPricingActionType = RentalEditorLine.Include ? RORKitPricingActionType::ModifyLine : RORKitPricingActionType::DeleteLine;
        }


        ttsBegin;
        rentalEditorLine.RORRentalLine::updateKitComponentPricing(kitPricingActionType, !lineAmountModified, forceUpdateFromComponent, parentUpdatedFromChild);
        lineAmountModified = false;
        ttsCommit;        

        rentalEditorLine.reread();
        rentalEditorLine_ds.research(true);
    }

    if (setNewPriceOnContract)
    {                      
        RentalEditorLine_ds.positionToRecord(rentalEditorLineParent);
        // This method is also called when a child line is edited ((un)checked) to update the parent
        // when this is the case the child line will be deleted, this variable will prevent it.
        parentUpdatedFromChild = true;

        switch (itemId)
        {
            case ragParameters.ItemIdInsurance :
                RentalEditorLine.InsurancePercentage = newPrice;
                break;

            case ragParameters.ItemIdWarranty :
                RentalEditorLine.WarrantyPrice = newPrice;
                break;

            case ragParameters.ItemIdTransport :
                RentalEditorLine.TransportPrice = newPrice;
                RentalEditorLine.TransportPriceDisc = lineDisc;
                break;

            case ragParameters.ItemIdReturnTransport :
                RentalEditorLine.ReturnTransportPrice = newPrice;
                RentalEditorLine.ReturnTransportDisc = lineDisc;
                break;
        }

        RentalEditorLine_ds.write();
        parentUpdatedFromChild = false;
    }

    if (childLinesUpdated)
    {
        RentalEditorLine_ds.research(true);
    }
}

void treeChange(RecId _selectedRecId)
{
    RORRentalEditorLine editorLineTmp;
    ;

    if(rentalEditorLine.RecId != _selectedRecId)
    {
        editorLineTmp = RORRentalEditorLine::findRecId(_selectedRecId);
        rentalEditorLine_ds.positionToRecord(editorLineTmp);
        rentalEditorLine_ds.refresh();
    }
}

public edit LineAmount editLineAmount(boolean _set, RORRentalEditorLine _editorLine, LineAmount _lineAmount)
{
    RentalEditorLine_ds.observe();
