}

public edit RORRentalNetAmount editNetAmount(boolean _set, RORRentalEditorLine _editorLine, RORRentalNetAmount _lineAmount)
{
    RentalEditorLine_ds.observe();

    RORRentalNetAmount netAmount = _editorLine.editNetAmount(_set, _lineAmount);

    if (_set)
    {
        RORRentalEditorLine editorLineFind;
        editorLineFind = RORRentalEditorLine::findRecId(_editorLine.RecId);
        rentalEditorLine_ds.positionToRecord(editorLineFind);
        rentalEditorLine_ds.refresh();
    }

    return netAmount;
}

public edit RORRentalDiscPercent editDiscPercent(boolean _set, RORRentalEditorLine _editorLine, RORRentalDiscPercent _discPercent)
{
    RentalEditorLine_ds.observe();

    RORRentalDiscPercent discPercent = _editorLine.editDiscPercent(_set, _discPercent);

    if (_set)
    {
        RORRentalEditorLine editorLineFind;
        editorLineFind = RORRentalEditorLine::findRecId(_editorLine.RecId);
        rentalEditorLine_ds.positionToRecord(editorLineFind);
        rentalEditorLine_ds.refresh();
    }

    return discPercent;
}

[SysClientCacheDataMethodAttribute(true)]
public display container displayExpandIconImage(RORRentalEditorLine _RentalEditorline)
{
    ImageReference imageReference;
    container imageContainer;
    boolean isExpanded;

    isExpanded = expandedRecords.exists(_RentalEditorline.RecId) ? true : false;
    imageReference = ImageReference::constructForSymbol(isExpanded ? ImageReferenceSymbol::CaretDown : ImageReferenceSymbol::CaretRight);

    if (imageReference)
    {
        imageContainer = imageReference.pack();
    }

    return imageContainer;
}

