public void modified()
{
    DialogButton    dialogButton;
    ;

    dialogButton = Box::yesNoCancel(strFmt("@DNR6681", UnitOfMeasure::find(RentalEditorLine.PriceTimeUnit).Symbol), dialogButton::Yes);

    switch (dialogButton)
    {
        case DialogButton::Cancel :
            RentalEditorLine.PriceTimeUnit = RentalEditorLine.orig().PriceTimeUnit;
            //go through
        case DialogButton::No :
            // just go to super()
            break;

        default :
            RentalEditorLine.modifiedPriceTimeUnit();
    }

    super();

    RentalEditorLine_ds.write();

    RentalEditorLine_ds.research(true);
}

