public void write()
{
    RORRentalEditorLine rentalEditorLineLocal;
    boolean             updateKitPricing = (RentalEditorHeader.InsuranceCalcBasis != RentalEditorHeader.orig().InsuranceCalcBasis
        || RentalEditorHeader.InsurancePercentage != RentalEditorHeader.orig().InsurancePercentage);

    super();

    if (updateKitPricing)
    {
        ttsBegin;

        select firstonly rentalEditorLineLocal
        where rentalEditorLineLocal.RefTableId == RentalEditorHeader.RefTableId
        && rentalEditorLineLocal.RefRecId == RentalEditorHeader.RefRecId;

        rentalEditorLineLocal.RORRentalLine::updateKitComponentPricing(RORKitPricingActionType::ModifyLine);
        rentalEditorLine_ds.research();
        rentalEditorLine_ds.positionToRecord(rentalEditorLineLocal);

        ttsCommit;
    }
}

