/// <summary>
/// Refreshing the RLE lines to show the prices
/// </summary>
public void clicked()
{
    super();

    RORRentalEditorLine rentalLineEditorLoc = RentalEditorLine_ds.cursor();
    rentalLineEditorLoc = RORRentalEditorLine::findRecId(rentalLineEditorLoc.RecId);

    RentalEditorLine_ds.executeQuery();

    RentalEditorLine_ds.findRecord(rentalLineEditorLoc);
}

public void clicked()
{
    super();

    if (expandedRecords.exists(RentalEditorLine.RecId))
    { 
        element.deleteFromMap(RentalEditorLine.RecId);
        rentalLineEditorV2.parmExpandedRecordsMap(expandedRecords);
    }
    else
    {
        rentalLineEditorV2.AddNextLevelSupplemetaryItems(RentalEditorLine.ItemId, RentalEditorLine, RentalEditorLine.KitLevel + 1);
        expandedRecords = rentalLineEditorV2.parmExpandedRecordsMap();
        tmpExpandedLines = rentalLineEditorV2.parmTMPExpandedRentalEditorLines();
        RORTmpExpandedRentalEditorLines.linkPhysicalTableInstance(tmpExpandedLines);
    }
    RentalEditorLine_ds.research(true);
}

public boolean modified()
{
    boolean ret;

    lineAmountModified = true;

    ret = super();

    return ret;
}

public void pageActivated()
{
    super();

    element.populateTreeControl();
}

public void selectionChanged(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    super(_oldItem, _newItem, _how);

    if(_oldItem.data())
    {

        treeSelectedRecId = _newItem.data();
        element.selectionChangedTimeOut();

    }
}
