private void removeSalesAgreement()
{
    if(salesTable.CustAccount && transferCurrencyAndDeliveryAddressFromSalesAgreement)
    {
        transferCurrencyAndDeliveryAddressFromSalesAgreement = false;
        element.unlinkSalesAgreement();
        salesTable_CustAccount.modified();
    }
}

private boolean canSalesAgreementBeLinked(SalesAgreementHeader _salesAgreementHeader, SalesAgreementId _salesAgreementId)
{
    if(!_salesAgreementId)
    {
        return false;
    }

    if(!_salesAgreementHeader)
    {
        checkFailed(strFmt("@SYS112224",
                        _salesAgreementId, "@SYS309959", tableId2pname(tableNum(SalesAgreementHeader))));
        return false;
    }

    if ((salesTable.CustAccount
                    && _salesAgreementHeader.CustAccount
                    && salesTable.CustAccount != _salesAgreementHeader.CustAccount)||
                (salesTable.InvoiceAccount
                    && _salesAgreementHeader.salesAgreementHeaderDefault().CustomerInvoiceAccount
                    && _salesAgreementHeader.salesAgreementHeaderDefault().CustomerInvoiceAccount != salesTable.InvoiceAccount))
    {
        checkFailed(strFmt("@SYS97692",
                        _salesAgreementHeader.SalesNumberSequence, "@SYS309959", tableId2pname(tableNum(SalesTable))));
        return false;
    }
    return true;
}

private void unlinkSalesAgreement()
{
    salesTable.MatchingAgreement = 0;
    salesTable.PaymentSched = '';
    salesTable.ProjId = '';
    salesTable.CustomerRef = '';
    salesTable.CashDiscPercent = 0.0;
    salesTable.ContactPersonId = '';
    salesTable.smmCampaignId = '';
    salesTable.SalesUnitId = '';
    salesTable.WorkerSalesResponsible = 0;
    salesTable.FixedExchRate = 0.0;
    salesTable.PurchOrderFormNum = '';
}

edit SalesAgreementId editSalesAgreementId(boolean _set, SalesAgreementId _salesAgreementId)
{
    SalesAgreementHeader    salesAgreementHeader;
    if (_set)
    {
        if (_salesAgreementId != salesAgreementId)
        {
            salesAgreementId = _salesAgreementId;
            salesAgreementHeader = SalesAgreementHeader::findAgreementId(salesAgreementId);

            if (!salesAgreementId && countryRegion_RU)
            {
                salesTable.DefaultDimension = AgreementHeaderExt_RU::clearAgreementDimension(salesTable.DefaultDimension);
            }

            if(!this.canSalesAgreementBeLinked(salesAgreementHeader, salesAgreementId))
            {
                this.removeSalesAgreement();
                return salesAgreementId;
            }

            if (salesAgreementHeader.RecId)
            {
                if (salesAgreementHeader.canReleaseAgreement(salesTable.ShippingDateRequested))
                {
                    if (Box::yesNo("@SYS305724",DialogButton::Yes) == DialogButton::Yes)
                    {
                        transferCurrencyAndDeliveryAddressFromSalesAgreement = true;

                        // Transfer default settings from Agreement to SalesTable buffer...
                        salesTable.initFromSalesAgreementHeader(salesAgreementHeader);

                        salesTable_CurrencyCode.update();
                        salesTable_ds.refresh();
                        salesTable_ds.rereadReferenceDataSources();
                    }

                    else
                    {
                        transferCurrencyAndDeliveryAddressFromSalesAgreement = false;
                        if (countryRegion_RU)
                        {
                            salesTable.DefaultDimension = salesTable.copyDimension(salesAgreementHeader.DefaultDimension);
                        }
                    }

                }
                else
                {
                    transferCurrencyAndDeliveryAddressFromSalesAgreement = false;
                    throw error("@SYS103075");
                }
            }
        }
    }

    return salesAgreementId;
}

