public void updateDesign()
{
    salesTable_ds.refresh();
    salesTable_ds.object(fieldNum(SalesTable, ProjId)).enabled(salesTable.WPPIsRentalProject && !salesTableForm.project());
    salesTable_ds.object(fieldNum(SalesTable, WPPIsRentalProject)).enabled(!salesTableForm.project());

    salesTable_ds.object(fieldNum(SalesTable, PurchOrderFormNum)).mandatory(CustTable::find(SalesTable.CustAccount).DNRCustRequisition[2] == DNRCustRequisitionLevel::Required);

    // Site and warehouse must be filled in if the rental order is being created from the service plan board. If this check is
    // not performed here, the check is eventually performed when the rental order line is being created. However, if the check fails
    // during the creation of the rental order line, the creation of the rental order fails and the user needs to repeat the process.
    if (salesTableForm.RORParmCalledFromServicePlanBoard())
    {
        salesTable_ds.object(fieldNum(SalesTable, InventSiteId)).mandatory(true);
        salesTable_ds.object(fieldNum(SalesTable, InventLocationId)).mandatory(true);
    }
}

public void initFromWorkLocation()
{
    RORVenueTable           venueTable;
    DNRAdditionalNoteTable  additionNote;

    select firstonly LocationNote, RouteNote from additionNote
        exists join venueTable
        where venueTable.VenueCode == salesTable.RORVenueCode
        &&    venueTable.RecId == additionNote.RefRecId
        &&    venueTable.TableId == additionNote.RefTableId;


    LocationNote.text(additionNote.LocationNote);
    RouteNote.text(additionNote.RouteNote);
}

