public boolean validate()
{
    boolean ret;
    CustTable custTableLocal = CustTable::find(salesTable.CustAccount);

    ret = super();

    // Validation to not allow a user to manually enter a phantom custAccount.
    // This was added at the datasource level instead of table level since only want
    // to validate and restrict use of phantom accounts at order entry and front end forms.
    if(custTableLocal.mcrMergedRoot)
    {
        checkFailed(strFmt("@MCR12337",salesTable.CustAccount,custTableLocal.mcrMergedRoot));
        // Return the parent account number
        salesTable.CustAccount = custTableLocal.mcrMergedRoot;
    }

    editSalesAgreementId.allowEdit(salesTable.CustAccount ? true : false);

    return ret;
}

public void modified()
{
    super();

    element.setInitialValuesDeliveryDate();

    if (salesTable.CustAccount)
    {
        salesTable_ds.object(fieldNum(SalesTable, OneTimeCustomer)).allowEdit(false);
        salesTable_ds.object(fieldNum(SalesTable, OneTimeCustomer)).skip(true);
    }
    else
    {
        salesTable_ds.object(fieldNum(SalesTable, OneTimeCustomer)).allowEdit(true);
        salesTable_ds.object(fieldNum(SalesTable, OneTimeCustomer)).skip(false);
    }

    if (countryRegion_RU)
    {
        salesTable.MatchingAgreement = 0;
        salesTable_RU.AgreementHeaderExt_RU = 0;
        salesTable.DefaultDimension = AgreementHeaderExt_RU::clearAgreementDimension(salesTable.DefaultDimension);
    }

    groupInterCompany.enabled(salesTable.custTable_CustAccount().interCompanyTradingRelationActive());
    salesTable_ds.object(fieldNum(SalesTable, DlvMode)).mandatory(salesTable.dlvModeMandatory());
    salesTable_ds.object(fieldNum(SalesTable, PurchOrderFormNum)).mandatory(!salesTable.type().DNRcheckCustRequisition(false));

    salesTable_ds.refresh();

    fromCustTable = salesTable.custTable_CustAccount();

    salesTable_ds.cacheCalculateMethod(tableMethodStr(SalesTable, customerName));
    salesTable_ds.cacheCalculateMethod(tableMethodStr(SalesTable, invoiceName));

    salesTable_ds.rereadReferenceDataSources();
    custTable_ds.executeQuery();
    mcrPrimaryAddressCust_ds.executeQuery();

    SalesTable_ds.updateDesign();
    element.mcrAllowCustomerEdit(false);

    // Validate that the invoice account is not merged
    if (salesTable.InvoiceAccount)
    {
        salesTable.validateField(fieldNum(SalesTable,InvoiceAccount));
    }

    // Set the expedite flag
    if (salesTable.DlvMode)
    {
        mcrSalesTableShipping.Expedite = DlvMode::find(salesTable.DlvMode).mcrExpedite;
        mcrSalesTableShipping_ds.refresh();
    }

    displayDNRAdditionalNote.text('');

    if (salesTable.CustAccount)
    {
        DNRAdditionalNoteTable additionalNoteTable;
        smmBusRelTable busRelTableLoc;
        CustTable custTableLoc;

        select firstonly OrderNote from additionalNoteTable
            exists join busRelTableLoc
            where busRelTableLoc.TableId == additionalNoteTable.RefTableId
            &&    busRelTableLoc.RecId == additionalNoteTable.RefRecId
                join TableId from custTableLoc
                where custTableLoc.Party == busRelTableLoc.Party
                &&    custTableLoc.AccountNum == salesTable.CustAccount;

        displayDNRAdditionalNote.text(additionalNoteTable.OrderNote);
    }

    mcrSalesTable.data( salesTable.mcrSalesTable());
    mcrSalesTable_ds.refresh();
}

