public display DirPartyName custDlvName()
{
    DirPartyLocation    dirPartyLocation;
    DirPartyTable       dirPartyTable;

    select firstonly Name from dirPartyTable
        where dirPartyTable.RecId == custTable.Party
            join Party, IsPrimary, IsPostalAddress from  dirPartyLocation
        where dirPartyLocation.Party == dirPartyTable.RecId
            && dirPartyLocation.IsPrimary == NoYes::Yes
            && dirPartyLocation.IsPostalAddress == NoYes::Yes;


    return dirPartyTable.Name;
}

