public boolean validateWrite()
{
    boolean ret = true;

    // If the 'allow expired source code' flag is not checked,
    // validate that the source code has not expired on replacement orders.
    if (mcrSalesTable.SourceId
        && salesTableForm.replacementOrder()
        && !MCROrderParameters::find().mcrAllowExpiredSourceCode)
    {
        ret = MCRSourceCodeSetup::validateSourceEffective(mcrSalesTable.SourceId);
    }

    ret = ret && super();

    return ret;
}

public boolean leaveRecord(boolean _forceUpdate = false)
{
    boolean ret;

    if(mcrSalesTable.SalesTable)
    {
        ret = super(_forceUpdate);
    }
    else
    {
        ret = true;
    }

    if (ret && mcrSalesTable.SourceId)
    {
        // Source code will be validated when the sales table is not return order.
        // If sales table is replacement order, source code will be validated when the 'allow expired source code' flag is unchecked.
        if (salesTable.SalesType != SalesType::ReturnItem
            && !(salesTableForm.replacementOrder() && MCROrderParameters::find().mcrAllowExpiredSourceCode))
        {
            ret = MCRSourceCodeSetup::validateSourceEffectivePublished(mcrSalesTable.SourceId);
        }
    }

    return ret;
}

