public void create(boolean _append = false)
{
    super(_append);

    if (createRentalDateTimes)
    {
        RORRentalDateTimes.initRentalDateTimes(SalesTable);

        if (salesTableForm.RORParmSelectionFromDateTime()
        &&  salesTableForm.RORParmSelectionToDateTime())
        {
            RORRentalDateTimes.OnRentDateTime = DateTimeUtil::removeTimeZoneOffset(salesTableForm.RORParmSelectionFromDateTime(), DateTimeUtil::getUserPreferredTimeZone());
            RORRentalDateTimes.modifiedField(fieldNum(RORRentalDateTimes, OnRentDateTime));
            RORRentalDateTimes.OffRentDateTime = DateTimeUtil::removeTimeZoneOffset(salesTableForm.RORParmSelectionToDateTime(), DateTimeUtil::getUserPreferredTimeZone());
            if (RORRentalDateTimes.validateField(fieldNum(RORRentalDateTimes, OffRentDateTime)))
            {
                RORRentalDateTimes.modifiedField(fieldNum(RORRentalDateTimes, OffRentDateTime));
            }
            else
            {
                warning("@DNR8596");
            }
        }

        RORRentalDateTimes.populateNonSqlFields();

        createRentalDateTimes = false;
    }
}

