public boolean modified()
{
    RORVenueTable       venueTable;
    smmBusRelTable      smmBusRelTable;
    smmBusRelTypeGroup  smmBusRelTypeGroup;

    boolean ret;

    ret = super();

    if (salesTableForm.project())
    {
        salesTable.WPPIsRentalProject = NoYes::Yes;
        salesTable.ProjId             = SalesTableForm.projId();
        salesTable.modifiedField(fieldNum(SalesTable, ProjId));
    }

    if (ret)
    {
        if (!salesTable.CustAccount && salesTable.RORVenueCode)
        {
            select venueTable
            where venueTable.VenueCode == salesTable.RORVenueCode
                join smmBusRelTable
                where smmBusRelTable.BusRelAccount == venueTable.smmBusRelAccount
                    join TypeId from smmBusRelTypeGroup
                    where smmBusRelTypeGroup.TypeId == smmBusRelTable.BusRelTypeId
                    &&    (smmBusRelTypeGroup.TableType == smmBusRelTypeSourceTable::Customer ||
                           smmBusRelTypeGroup.TableType == smmBusRelTypeSourceTable::IRMCustVend);

            if (smmBusRelTable)
            {
                salesTable.CustAccount = CustTable::findByPartyRecId(smmBusRelTable.Party).AccountNum;
                salesTable.modifiedField(fieldNum(SalesTable, CustAccount));
                salesTable_ds.object(fieldNum(SalesTable, CustAccount)).modified();
                salesTable.RORinitFromVenueTable(venueTable, !salesTableForm.project());
            }
        }

        salesTable_ds.updateDesign();
    }

    return ret;
}

