public void lookup()
{
    SalesAgreementHeader::salesAgreementLookup(this, salesTable);
}

public boolean modified()
{
    boolean ret;

    // Prevent the question to unlink the project and order to popup
    ret = salesTableForm.project() ? true : super();

    if (ret)
    {
        salesTable_ds.updateDesign();
    }

    return ret;
}

public void lookup()
{
    if (salesTable.CustAccount)
    {
        ProjTable::RORLookupWithCustAccount(this, salesTable.CustAccount);
    }
    else
    {
        super();
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (this.value() == NoYes::Yes)
    {
        salesTable.ReceiptDateConfirmed  = salesTable.ReceiptDateRequested;
        salesTAble.ShippingDateConfirmed = salesTAble.ShippingDateRequested;
    }
    else
    {
        salesTable.ReceiptDateConfirmed  = dateNull();
        salesTAble.ShippingDateConfirmed = dateNull();
    }

    return ret;
}

void clicked()
{
    if (salesTable.type().DNRcheckCustRequisition()
    &&  salesTable_ds.validateWrite())
    {
        // Can't update if it hasn't been created yet
        if (salesTable.RecId)
        {
            salesTable.update();//super only calls .doUpdate, we need the update logic including .orig logic
        }
        super();
    }
}
