boolean canClose()
{
    boolean                         canClose;
    SalesQuotationPriceConversion   quotationPriceConversion;
    SalesCalcAvailableDlvDates      salesCalcAvailableDlvDates;
    boolean                         datesOK = true;

    if (element.closedOk())
    {
        datesOK = isConfigurationkeyEnabled(configurationKeyNum(RORRentalDateTimes)) && RORRentalDateTimes.validateWrite();
    }

    if (!element.closedOk() || datesOK)
    {
        canClose = super();
    }

    if (element.closedOk() && canClose
    &&  salesQuotationTable.DNRcheckCustRequisition())
    {
        quotationPriceConversion = salesQuotationPriceConversion.selection() == 0 ? SalesQuotationPriceConversion::Current : SalesQuotationPriceConversion::Template;
        salesCalcAvailableDlvDates = SalesCalcAvailableDlvDates::newCommonSalesDlvDateType(salesQuotationTable);
        canClose = salesCalcAvailableDlvDates.validateWritePrompt(salesQuotationTable,false);

        salesQuotationTableForm.parmFromTemplateGroupId(salesQuotationTable.TemplateGroupId);
        salesQuotationTableForm.parmFromTemplateName(salesQuotationTable.TemplateName);
        salesQuotationTableForm.parmQuotationPriceConversion(quotationPriceConversion);

        salesQuotationTable.TemplateActive      = NoYes::No;
        salesQuotationTable.TemplateGroupId     = '';
        salesQuotationTable.TemplateName        = '';

        salesQuotationTable.doUpdate();
    }

    return canClose;
}

void changeType(QuotationType _quotationType)
{
    NumberSeqFormHandler numberSeqFormHandlerQuotationId;

    if (salesQuotationTableType)
    {
        numberSeqFormHandlerQuotationId = salesQuotationTableType.parmNumberSeqFormHandlerQuotationId();
        salesQuotationTableType.formMethodQuotationTypeModifyPreChange(element, salesQuotationTable_ds);
    }

    salesCreateQuotationForm = SalesCreateQuotationForm::construct(_quotationType);

    salesQuotationTableType = salesQuotationTable.type();
    salesQuotationTableType.parmNumberSeqFormHandlerQuotationId(numberSeqFormHandlerQuotationId);
    salesQuotationTableType.formMethodQuotationTypeModifyPostChange(element,salesQuotationTable_ds);
}

