void run()
{
    ProjInvoiceTable    projInvoiceTable;
    MCRCustSearchType   mcrCustSearchType;

    salesQuotationTable_ds.create();
    createRentalDateTimes = isConfigurationkeyEnabled(configurationKeyNum(RORRentalDateTimes));
    RORRentalDateTimes_ds.create();
    element.modifyForm();

    // Set currency field
    if (salesQuotationTableForm.parmSource().TableId == tableNum(ProjInvoiceTable))
    {
        projInvoiceTable = salesQuotationTableForm.parmSource() as ProjInvoiceTable;
        salesQuotationTable.CurrencyCode = projInvoiceTable.CurrencyId;
        salesQuotationTable_CurrencyCode.text(projInvoiceTable.CurrencyId);
    }

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(TradeCustomerSearch)))
    {
        // Check if the parameter is set to load the last saved
        if (MCRFullTextParameters::find().SaveSearchType)
        {
            // Get the user's saved search type, if it's not default, set it.
            mcrCustSearchType = MCRCustSearch::getCustSearchType();
            if (mcrCustSearchType)
            {
                custSearchType.selection(enum2int(mcrCustSearchType));
            }
        }

        // set focus to customer search when entering form
        custTable_MCRSearchText.setFocus();
    }
    else
    {
        if (prospect)
        {
            salesQuotationTable_ds.lookUpBusRelAccount();
        }
        else
        {
            salesQuotationTable_ds.lookUpCustAccount();
        }
    }

    if (!salesQuotationTableType)
    {
        element.close();
        throw Exception::Error;
    }
}

public void setCustomerRequisition()
{
    int custReqIdx;

    if (salesQuotationTable.CustAccount && AccountType.selection() == smmQuotationAccountType::CustAccount)
    {
        if (salesQuotationTable.QuotationType == QuotationType::RORProjRental)
        {
            custReqIdx = 4;
        }
        else
        {
            custReqIdx = 2;
        }

        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CustPurchaseOrder)).mandatory(CustTable::DNRIsCustRequisitionMandatory(salesQuotationTable.CustAccount, '', custReqIdx));
    }
    else
    {
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CustPurchaseOrder)).mandatory(false);
    }
}

void setFieldsActive()
{
    boolean     showRental          = createQuotationType == QuotationType::RORRental;
    boolean     showProject         = createQuotationType == QuotationType::RORProjRental;

    // Set up fields according to selection
    groupCustomer.visible(showRental || showProject);
    groupReferences.visible(showRental || showProject);
    tabAdministration.visible(showRental || showProject);

    if (prospect)
    {
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, BusRelAccount)).mandatory(showRental || showProject);
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CustAccount)).mandatory(false);
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, InvoiceAccount)).mandatory(false);
    }
    else
    {
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, BusRelAccount)).mandatory(false);
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CustAccount)).mandatory(showRental || showProject);
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, InvoiceAccount)).mandatory(showRental || showProject);
    }

    // General group
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, ReceiptDateRequested)).enabled(showRental || showProject);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, ShippingDateRequested)).enabled(showRental || showProject);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, QuotationExpiryDate)).enabled(showRental || showProject);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CurrencyCode)).enabled(true);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, DlvMode)).enabled(showRental || showProject);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, InventLocationId)).enabled(showRental || showProject);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, DeliveryDateControlType)).enabled(showRental || showProject);

    // Template fields
    salesAvailableDlvDates.enabled(showRental || showProject);

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable,InvoiceAccount)).allowEdit(!prospect);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable,ProjIdRef)).allowEdit(!prospect);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable,ProjInvoiceProjId)).allowEdit(!prospect);
}

