public void initFromWorkLocation()
{
    RORVenueTable           venueTable;
    DNRAdditionalNoteTable  additionNote;

    select firstonly LocationNote, RouteNote from additionNote
        exists join venueTable
        where venueTable.VenueCode == salesQuotationTable.RORVenueCode
        &&    venueTable.RecId == additionNote.RefRecId
        &&    venueTable.TableId == additionNote.RefTableId;


    LocationNote.text(additionNote.LocationNote);
    RouteNote.text(additionNote.RouteNote);
}

public boolean validate()
{
    MCRsmmBusRelTable  mcRsmmBusRelTable;
    boolean         ret = super();

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        // switch to surviving business relation when the buisness relation entered has been merged and
        // notify user of switch.
        mcRsmmBusRelTable = MCRsmmBusRelTable::findAccount(salesQuotationTable.BusRelAccount);
        if(mcRsmmBusRelTable.MergedRoot)
        {
            checkFailed(strFmt("@MCR12337", salesQuotationTable.BusRelAccount, mcRsmmBusRelTable.MergedRoot));
            salesQuotationTable.BusRelAccount = mcRsmmBusRelTable.MergedRoot;
        }

        if(mcRsmmBusRelTable.IsListDeleted)
        {
            ret = checkFailed("@MCR26132");
        }
    }

    return ret;
}

public void modified()
{
    CustTable custTable;

    super();

    element.setFieldsActive();
    salesQuotationTable_ds.refresh();
    salesQuotationTable_ds.rereadReferenceDataSources();
    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        // Validate the customer account and invoice account
        if (!salesQuotationTable.validateField(fieldNum(SalesQuotationTable, CustAccount)))
        {
            custTable = CustTable::find(salesQuotationTable.CustAccount);
            // Return the parent account number
            salesQuotationTable.CustAccount = custTable.mcrMergedRoot;
            salesQuotationTable.modifiedField(fieldNum(SalesQuotationTable, CustAccount));
        }
        salesQuotationTable.validateField(fieldNum(SalesQuotationTable, InvoiceAccount));
    }

    displayDNRAdditionalNote.text('');

    if (salesQuotationTable.BusRelAccount)
    {
        DNRAdditionalNoteTable additionalNoteTable;
        smmBusRelTable busRelTable;

        select firstonly OrderNote from additionalNoteTable
            exists join busRelTable
            where busRelTable.BusRelAccount == salesQuotationTable.BusRelAccount
            &&    busRelTable.TableId == additionalNoteTable.RefTableId
            &&    busRelTable.RecId == additionalNoteTable.RefRecId;

        displayDNRAdditionalNote.text(additionalNoteTable.OrderNote);
    }
}

