    SalesQuotationTable::lookupBusRelAccount(_formControl);
}

/// <summary>
///     Validates that the user entered customer account is not a merged account.
/// </summary>
/// <returns>
///     true if the customer account entered is not a merged account; otherwise, false.
/// </returns>
/// <remarks>
///     Validation to not allow a user to manually enter a phantom custAccount.
///     This was added at the datasource level instead of table level since only want
///     to validate and restrict use of phantom accounts at order entry and front end forms.
/// </remarks>
public boolean validate()
{
    CustTable custTable = salesQuotationTable.custTable_CustAccount();
    boolean ret = super();

    if (custTable.mcrMergedParent)
    {
        // Warning displayed in validateField on the salesQuotationTable
        // Return the parent account number
        salesQuotationTable.CustAccount = custTable.mcrMergedRoot;
    }

    return ret;
}

public void  modified()
{
    super();

    element.setCustomerRequisition();

    element.setInitialValuesDeliveryDate();

    salesQuotationTable_ds.refresh();
    salesQuotationTable_ds.rereadReferenceDataSources();
    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        // Validate that the invoice account is not merged
        salesQuotationTable.validateField(fieldNum(SalesQuotationTable,InvoiceAccount));
    }

    displayDNRAdditionalNote.text('');

    if (salesQuotationTable.CustAccount)
    {
        DNRAdditionalNoteTable additionalNoteTable;
        smmBusRelTable busRelTable;
        CustTable custTable;

        select firstonly OrderNote from additionalNoteTable
            exists join busRelTable
            where busRelTable.TableId == additionalNoteTable.RefTableId
            &&    busRelTable.RecId == additionalNoteTable.RefRecId
                join TableId from custTable
                where custTable.Party == busRelTable.Party
                &&    custTable.AccountNum == salesQuotationTable.CustAccount;

        displayDNRAdditionalNote.text(additionalNoteTable.OrderNote);
    }
}

