/// <summary>
///     Validates that the user entered customer account is not a merged account.
/// </summary>
/// <returns>
///     true if the customer account entered is not a merged account; otherwise, false.
/// </returns>
/// <remarks>
///     Validation to not allow a user to manually enter a phantom custAccount.
///     This was added at the datasource level instead of table level since only want
///     to validate and restrict use of phantom accounts at order entry and front end forms.
/// </remarks>
public boolean validate()
{
    CustTable custTable = salesQuotationTable.custTable_InvoiceAccount();
    boolean ret = super();

    if (custTable.mcrMergedParent)
    {
        ret = false;
    }

    return ret;
}

