public boolean validate()
{
    boolean ret = super();

    if(!ret)
    {
        return ret;
    }

    if(!salesQuotationTable.isTemplate())
    {
        // template must be active
        if(salesQuotationTable.TemplateGroupId && !SalesQuotationTemplateGroup::hasActiveTemplates(salesQuotationTable.TemplateGroupId, salesQuotationTable.QuotationType))
        {
            // Template Group does not have active templates.
            ret = checkFailed(strFmt("@SYS99925", salesQuotationTable.TemplateGroupId));
        }
    }

    return ret;
}

public boolean validate()
{
    boolean ret = super();

    if (ret)
    {
        if (! SalesQuotationTable::existTemplate(salesQuotationTable.QuotationType,salesQuotationTable.TemplateGroupId, salesQuotationTable.TemplateName))
        {
            // Template does not exist.
            ret = checkFailed("@SYS30623");
        }

        // template must be active
        if(ret && salesQuotationTable.TemplateName && SalesQuotationTable::findTemplate(salesQuotationTable.QuotationType,salesQuotationTable.TemplateGroupId, salesQuotationTable.TemplateName).TemplateActive == NoYes::No)
        {
            // Template is not active.
            ret = checkFailed("@SYS99743");
        }
    }

    return ret;
}

