public void create(boolean _append = false)
{
    super(_append);

    if (createRentalDateTimes)
    {
        RORRentalDateTimes.initRentalDateTimes(SalesQuotationTable);

        if (salesQuotationTableForm.RORParmSelectionFromDateTime()
        &&  salesQuotationTableForm.RORParmSelectionToDateTime())
        {
            RORRentalDateTimes.OnRentDateTime = DateTimeUtil::removeTimeZoneOffset(salesQuotationTableForm.RORParmSelectionFromDateTime(), DateTimeUtil::getUserPreferredTimeZone());
            RORRentalDateTimes.modifiedField(fieldNum(RORRentalDateTimes, OnRentDateTime));
            RORRentalDateTimes.OffRentDateTime = DateTimeUtil::removeTimeZoneOffset(salesQuotationTableForm.RORParmSelectionToDateTime(), DateTimeUtil::getUserPreferredTimeZone());
            RORRentalDateTimes.modifiedField(fieldNum(RORRentalDateTimes, OffRentDateTime));
        }

        RORRentalDateTimes.initFromSalesQuotationTable(SalesQuotationTable);
        RORRentalDateTimes.populateNonSqlFields();

        createRentalDateTimes = false;
    }
}

