public boolean modified()
{
    boolean             ret;
    ContactPersonName   operator = ROREditContactPersonNameOperator.text();

    //smmInit::construct();

    ret = super();


    // Check if the input exists in ContactPerson.Name
    if (ContactPerson::find(operator).personName())
    {
        // Set the contactPersonName with the ContactPerson.Name
        contactPersonName = ContactPerson::find(operator).personName();
    }
    else
    {
        // Set the contactPersonName with the input text.
        contactPersonName = operator;
    }

    if (!ContactPerson::findNameParty(salesQuotationTable.party(), contactPersonName))
    {
        salesQuotationTable.RORContactPersonIdOperator = ContactPerson::findOrCreateNameParty(salesQuotationTable.party(), operator).ContactPersonId;
    }

    return ret;
}

