public boolean modified()
{
    RORVenueTable       venueTable;
    smmBusRelTable      smmBusRelTable;
    smmBusRelTypeGroup  smmBusRelTypeGroup;

    boolean ret;

    ret = super();

    if (ret)
    {
        if (!salesQuotationTable.CustAccount && !SalesQuotationTable.BusRelAccount && salesQuotationTable.RORVenueCode)
        {
            select venueTable
            where venueTable.VenueCode == salesQuotationTable.RORVenueCode
                join smmBusRelTable
                where smmBusRelTable.BusRelAccount == venueTable.smmBusRelAccount
                    join TableType from smmBusRelTypeGroup
                    where smmBusRelTypeGroup.TypeId == smmBusRelTable.BusRelTypeId
                    &&    (smmBusRelTypeGroup.TableType == smmBusRelTypeSourceTable::Customer
                    ||     smmBusRelTypeGroup.TableType == smmBusRelTypeSourceTable::IRMCustVend
                    ||     smmBusRelTypeGroup.TableType == smmBusRelTypeSourceTable::Relation);

            if (smmBusRelTable)
            {
                if (smmBusRelTypeGroup.TableType == smmBusRelTypeSourceTable::Customer
                ||  smmBusRelTypeGroup.TableType == smmBusRelTypeSourceTable::IRMCustVend)
                {
                    AccountType.selection(smmQuotationAccountType::CustAccount);
                    salesQuotationTable.CustAccount = CustTable::findByPartyRecId(smmBusRelTable.Party).AccountNum;
                    salesQuotationTable.modifiedField(fieldNum(SalesQuotationTable, CustAccount));
                    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CustAccount)).modified();
                    prospect = false;
                }
                else
                {
                    AccountType.selection(smmQuotationAccountType::BusRelAccount);
                    salesQuotationTable.BusRelAccount = smmBusRelTable.BusRelAccount;
                    salesQuotationTable.modifiedField(fieldNum(SalesQuotationTable, BusRelAccount));
                    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, BusRelAccount)).modified();
                    prospect = true;
                }

                salesQuotationTable.RORinitFromVenueTable(venueTable);
                salesQuotationTable_ds.updateDesign();
            }
        }
    }

    return ret;
}

