/// <summary>
/// Opens the related taskpage to edit a record.
/// </summary>
/// <remarks>
/// Different taskpages are opened depending on the type of the Quotation.
/// </remarks>
void clicked()
{
    Args args;

    super();

    args = new Args();
    if (salesQuotationTable.RecId != 0)
    {

        args.record(salesQuotationTable);
        args.caller(element);
        args.parmObject(element);
        args.openMode(OpenMode::Edit);
        args.copyCallerQuery(CopyCallerQuery::Yes);
        switch(salesQuotationTable.QuotationType)
        {
            case QuotationType::RORProjRental:
                new MenuFunction(menuitemDisplayStr(RORSalesQuotationProjTableForEdit), MenuItemType::Display).run(args);
                break;

            default:
                new MenuFunction(menuitemDisplayStr(RORSalesQuotationTableForEdit), MenuItemType::Display).run(args);
                break;
        }
    }

}

