/// Initializes ranges on SalesQuotationTable
/// </summary>
void initRanges()
{
    QueryBuildDataSource queryBuildDataSource = salesQuotationTable_ds.query().dataSourceTable(tableNum(SalesQuotationTable));

    SysQuery::findOrCreateRange(queryBuildDataSource,fieldNum(SalesQuotationTable, QuotationType)).value(int2str(QuotationType::RORProjRental));
}

