public boolean validate()
{
    boolean ret;
    CustTable   custTableLocal;

    ret = super();

    if (ret && salesQuotationTable.OpportunityId)
    {
        // Changing either the business account or customer account will clear the opportunity ID. Do you want to continue?
        ret = ((Box::okCancel("@SYS112839",DialogButton::Cancel) == DialogButton::Ok ) ? true : false);
    }
    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        // Switch to surviving BR when BR entered has been merged and notify user of switch.
        custTableLocal = CustTable::find(salesQuotationTable.CustAccount);
        if (custTableLocal.mcrMergedRoot)
        {
            checkFailed(strFmt("@MCR12337", salesQuotationTable.BusRelAccount, custTableLocal.mcrMergedRoot));
            salesQuotationTable.BusRelAccount = custTableLocal.mcrMergedRoot;
        }
    }

    return ret;
}

public void modified()
{
    smmQuotationAlternativeQuotations   smmQuotationAlternativeQuotations;


    if (crmLicense)
    {
        if (smmQuotationAlternativeQuotations::exist(salesQuotationTable.QuotationId))
        {
            if (Box::yesNo("@SYS90096" + " " + "@SYS41157",DialogButton::No,"@SYS23828","@SYS23828") == DialogButton::Yes)
            {
                ttsbegin;

                delete_from smmQuotationAlternativeQuotations
                    where smmQuotationAlternativeQuotations.QuotationId == salesQuotationTable.QuotationId;

                ttscommit;
            }
            else
            {
                salesQuotationTable.CustAccount     = salesQuotationTable.orig().CustAccount;
            }
        }
        else if (smmQuotationAlternativeQuotations::existLinked(salesQuotationTable.QuotationId))
        {
            if (Box::yesNo("@SYS90096" + " " + "@SYS41157",DialogButton::No,"@SYS23828","@SYS23828") == DialogButton::Yes)
            {
                ttsbegin;

                select forupdate smmQuotationAlternativeQuotations
                    where smmQuotationAlternativeQuotations.LinkedQuotationId == salesQuotationTable.QuotationId;
                smmQuotationAlternativeQuotations.delete();

                ttscommit;
            }
            else
            {
                salesQuotationTable.CustAccount     = salesQuotationTable.orig().CustAccount;
            }
        }

        if (salesQuotationTable.CustAccount != '' &&
            salesQuotationTable.CustAccount != salesQuotationTable.orig().CustAccount)
        {
            super();
            element.setFieldAccessHeader();
        }
    }
    else
    {
        super();
        element.setFieldAccessHeader();
    }
}

